int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, X;
    cin >> n >> X;
    vector<int> p(n + 1);
    for (int i = 0; i <= n; ++i) {
        cin >> p[i];
    }
    double c = X / 1e6;

    vector<vector<double>> E(n + 1, vector<double>(n + 1, 0.0));

    for (int s = 1; s <= n; ++s) {
        for (int r = 0; r <= s; ++r) {
            int b = s - r;
            if (r + b != s) continue;
            if (r > n || b > n) continue;

            double current_ratio = (s == 0) ? 0.0 : (double)r / s;
            if (current_ratio < c - 1e-12) {
                E[r][b] = 0.0;
                continue;
            }

            double val = 0.0;
            if (r > 0) {
                val += (r / (double)s) * (1 + E[r - 1][b]);
            }
            if (b > 0) {
                val += (b / (double)s) * E[r][b - 1];
            }
            val -= c;
            E[r][b] = max(0.0, val);
        }
    }

    double res = 0.0;
    for (int i = 0; i <= n; ++i) {
        res += (p[i] / 1e6) * E[i][n - i];
    }

    cout << fixed << setprecision(10) << res << endl;

    return 0;
}

