using namespace std;

vector<int> merge(const vector<int>& a, const vector<int>& b) {
    if (a.empty()) return b;
    if (b.empty()) return a;

    int a_start = a[0];
    int a_end = a.back();
    int b_start = b[0];
    int b_end = b.back();

    vector<int> merged;

    if (are_connected({a_start}, {b_start})) {
        merged = a;
        reverse(merged.begin(), merged.end());
        vector<int> reversed_b = b;
        reverse(reversed_b.begin(), reversed_b.end());
        merged.insert(merged.end(), reversed_b.begin(), reversed_b.end());
    } else if (are_connected({a_start}, {b_end})) {
        merged = a;
        reverse(merged.begin(), merged.end());
        merged.insert(merged.end(), b.begin(), b.end());
    } else if (are_connected({a_end}, {b_start})) {
        merged = a;
        merged.insert(merged.end(), b.begin(), b.end());
    } else if (are_connected({a_end}, {b_end})) {
        merged = a;
        vector<int> reversed_b = b;
        reverse(reversed_b.begin(), reversed_b.end());
        merged.insert(merged.end(), reversed_b.begin(), reversed_b.end());
    } else {
        return a.size() >= b.size() ? a : b;
    }

    return merged;
}

vector<int> longest_trip(int N, int D) {
    if (N <= 0) return {};
    if (N == 1) return {0};

    int m = (N - 1) / 2;
    vector<int> left = longest_trip(m + 1, D);
    vector<int> right = longest_trip(N - (m + 1), D);

    for (auto& x : right) {
        x += m + 1;
    }

    return merge(left, right);
}

