using namespace std;

bool is_connected(pair<int, int> u, pair<int, int> v, const vector<vector<bool>>& grid) {
    if (u.first == v.first) { // same row
        int r = u.first;
        int c1 = u.second;
        int c2 = v.second;
        if (c1 > c2) swap(c1, c2);
        for (int c = c1; c <= c2; ++c) {
            if (!grid[r][c]) {
                return false;
            }
        }
        return true;
    } else if (u.second == v.second) { // same column
        int c = u.second;
        int r1 = u.first;
        int r2 = v.first;
        if (r1 > r2) swap(r1, r2);
        for (int r = r1; r <= r2; ++r) {
            if (!grid[r][c]) {
                return false;
            }
        }
        return true;
    } else {
        return false;
    }
}

int biggest_stadium(int N, vector<vector<int>> F) {
    vector<pair<int, int>> empty_cells;
    for (int r = 0; r < N; ++r) {
        for (int c = 0; c < N; ++c) {
            if (F[r][c] == 0) {
                empty_cells.emplace_back(r, c);
            }
        }
    }
    int m = empty_cells.size();
    for (int s = m; s >= 1; --s) {
        for (int mask = 0; mask < (1 << m); ++mask) {
            if (__builtin_popcount(mask) != s) continue;
            vector<pair<int, int>> subset;
            for (int i = 0; i < m; ++i) {
                if (mask & (1 << i)) {
                    subset.push_back(empty_cells[i]);
                }
            }
            vector<vector<bool>> grid(N, vector<bool>(N, false));
            for (auto& cell : subset) {
                grid[cell.first][cell.second] = true;
            }
            bool valid = true;
            for (int i = 0; i < subset.size() && valid; ++i) {
                for (int j = i + 1; j < subset.size(); ++j) {
                    auto& u = subset[i];
                    auto& v = subset[j];
                    if (is_connected(u, v, grid)) continue;
                    bool found = false;
                    for (int k = 0; k < subset.size(); ++k) {
                        auto& w = subset[k];
                        if (is_connected(u, w, grid) && is_connected(w, v, grid)) {
                            found = true;
                            break;
                        }
                    }
                    if (!found) {
                        valid = false;
                        break;
                    }
                }
            }
            if (valid) {
                return s;
            }
        }
    }
    return 1; // Shouldn't reach here as per problem statement
}

