int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int t;
    cin >> t;

    while (t--) {
        int n;
        cin >> n;

        vector<int> a(n);
        for (int &x : a) {
            cin >> x;
        }

        reverse(a.begin(), a.end());

        bool possible = true;
        long long pre_prev = 1e18;
        long long suf_rev_prev = -1e18;

        for (int x : a) {
            long long current_pre = min(pre_prev, x - suf_rev_prev);
            current_pre = min(current_pre, (long long)x);
            if (current_pre < 0) {
                possible = false;
                break;
            }

            long long current_suf_rev = x - current_pre;

            if (current_suf_rev < suf_rev_prev) {
                possible = false;
