const int INF = 1e9;

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    string s, t;
    cin >> s >> t;
    if (s.size() != t.size()) {
        cout << -1 << '\n';
        return 0;
    }

    vector<vector<int>> cost(26, vector<int>(26, INF));
    for (int i = 0; i < 26; ++i)
        cost[i][i] = 0;

    int n;
    cin >> n;
    for (int i = 0; i < n; ++i) {
        char a, b;
        int w;
        cin >> a >> b >> w;
        int u = a - 'a';
        int v = b - 'a';
        if (cost[u][v] > w)
            cost[u][v] = w;
    }

    for (int k = 0; k < 26; ++k)
        for (int i = 0; i < 26; ++i)
            for (int j = 0; j < 26; ++j)
                if (cost[i][k] + cost[k][j] < cost[i][j])
                    cost[i][j] = cost[i][k] + cost[k][j];

    long long total = 0;
    string res;

    for (int i = 0; i < s.size(); ++i) {
        char a = s[i], b = t[i];
        if (a == b) {
            res.push_back(a);
            continue;
        }

        int u = a - 'a', v = b - 'a';
        int best_c = -1, min_sum = INF;

        for (int c = 0; c < 26; ++c) {
            int sum = cost[u][c] + cost[v][c];
            if (sum < min_sum) {
                min_sum = sum;
                best_c = c;
            }
        }

        if (min_sum == INF) {
            cout << -1 << '\n';
            return 0;
        }

        total += min_sum;
        res.push_back('a' + best_c);
    }

    cout << total << '\n' << res << '\n';
    return 0;
}

