using namespace std;

int n;
vector<int> s, p, w, l;
vector<long long> total_gain;
vector<int> distinct_s;
int S_max;

void init(int _n, vector<int> _s, vector<int> _p, vector<int> _w, vector<int> _l) {
    n = _n;
    s = _s;
    p = _p;
    w = _w;
    l = _l;

    // Precompute distinct s values
    unordered_set<int> s_set;
    for (int val : s) {
        s_set.insert(val);
    }
    distinct_s = vector<int>(s_set.begin(), s_set.end());
    sort(distinct_s.begin(), distinct_s.end());
    if (distinct_s.empty()) {
        S_max = 0;
    } else {
        S_max = distinct_s.back();
    }

    // Precompute total_gain for each dungeon when strength >= S_max
    total_gain.resize(n);
    for (int i = n-1; i >= 0; --i) {
        if (w[i] == n) {
            total_gain[i] = s[i];
        } else {
            total_gain[i] = s[i] + total_gain[w[i]];
        }
    }
}

long long simulate(int x, int z) {
    long long current_strength = z;
    int current_dungeon = x;

    while (current_dungeon != n) {
        if (current_strength >= S_max) {
            current_strength += total_gain[current_dungeon];
            break;
        }

        // Determine current phase
        int Sj = 0;
        for (int val : distinct_s) {
            if (val <= current_strength) {
                Sj = val;
            } else {
                break;
            }
        }
        int Sj_next = S_max;
        for (int val : distinct_s) {
            if (val > Sj) {
                Sj_next = val;
                break;
            }
        }

        if (current_strength >= Sj_next) {
            continue;
        }

        int i = current_dungeon;
        if (s[i] <= Sj) {
            current_strength += s[i];
            current_dungeon = w[i];
        } else {
            if (l[i] == i) {
                // Handle loop case
                int required = min(s[i], Sj_next);
                if (current_strength >= required) {
                    current_strength += p[i];
                    current_dungeon = l[i];
                    if (current_strength >= s[i]) {
                        current_strength += s[i];
                        current_dungeon = w[i];
                    }
                } else {
                    long long needed = required - current_strength;
                    long long steps = (needed + p[i] - 1) / p[i];
                    current_strength += steps * p[i];
                    current_dungeon = l[i];
                    if (current_strength >= s[i]) {
                        current_strength += s[i];
                        current_dungeon = w[i];
                    }
                }
            } else {
                current_strength += p[i];
                current_dungeon = l[i];
            }
        }
    }
    return current_strength;
}

