#include <bits/stdc++.h>
using namespace std;
using ll = long long;

struct Triplet {
    ll g, x, y;
};

Triplet extended_gcd(ll a, ll b) {
    if (a == 0) return {b, 0, 1};
    auto [g, x, y] = extended_gcd(b % a, a);
    return {g, y - (b/a)*x, x};
}

ll ceil_div(ll a, ll b) {
    if (b == 0) return 0;
    if (a % b == 0) return a / b;
    if (b < 0) return ceil_div(-a, -b);
    return (a + b - 1) / b;
}

void compute_x_y(ll n, ll k, ll& x, ll& y) {
    if (k == 3) {
        x = 1;
        y = 1;
        return;
    }
    x = y = -1;
    ll a = 0, b = 1;
    ll current_m = 2;
    while (current_m <= k-1) {
        ll next_fib = a + b;
        if (current_m == k-2) x = next_fib;
        if (current_m == k-1) y = next_fib;
        if (next_fib > n) break;
        a = b;
        b = next_fib;
        current_m++;
    }
    if (current_m < k-2) {
        x = n + 1;
        y = n + 1;
    } else if (current_m == k-2) {
        y = a + b;
    }
}

void solve() {
    ll t;
    cin >> t;
    while (t--) {
        ll n, k;
        cin >> n >> k;
        if (k < 3) {
            cout << "0\n";
            continue;
        }
        ll x, y;
        compute_x_y(n, k, x, y);
        if (x > n) {
            if (y != 0 && n % y == 0) {
                ll b_val = n / y;
                if (b_val >= 0) cout << "1\n";
                else cout << "0\n";
            } else cout << "0\n";
            continue;
        }
        if (y > n) {
            cout << "0\n";
            continue;
        }
        ll d = gcd(x, y);
        if (n % d != 0) {
            cout << "0\n";
            continue;
        }
        ll x_prime = x / d;
        ll y_prime = y / d;
        ll n_prime = n / d;
        if (x_prime == 1) {
            ll min_b = ceil_div(n_prime, y_prime + 1);
            ll max_b = n_prime / y_prime;
            min_b = max(min_b, 0LL);
            cout << max(0LL, max_b - min_b + 1) << "\n";
            continue;
        }
        auto [g, inv, tmp] = extended_gcd(y_prime, x_prime);
        if (g != 1) {
            cout << "0\n";
            continue;
        }
        inv = (inv % x_prime + x_prime) % x_prime;
        ll b0 = (n_prime * inv) % x_prime;
        ll a0 = (n_prime - y_prime * b0) / x_prime;
        ll t1 = ceil_div(a0 - b0, x_prime + y_prime);
        ll t2 = ceil_div(-b0, x_prime);
        ll n_over_xy = ceil_div(n_prime, x_prime + y_prime);
        ll t3 = ceil_div(n_over_xy - b0, x_prime);
        ll t_lower = max({t1, t2, t3});
        ll t4 = a0 / y_prime;
        ll t5 = (n_prime / y_prime - b0) / x_prime;
        ll t_upper = min(t4, t5);
        if (t_lower > t_upper) cout << "0\n";
        else cout << t_upper - t_lower + 1 << "\n";
    }
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
    solve();
    return 0;
}

