#include <bits/stdc++.h>
using namespace std;

using ll = long long;

vector<int> color;
vector<vector<int>> adj;

bool isBipartite(int start, ll& a, ll& b) {
    queue<int> q;
    q.push(start);
    color[start] = 0;
    a = 1;
    b = 0;
    bool bipart = true;

    while (!q.empty()) {
        int u = q.front();
        q.pop();

        for (int v : adj[u]) {
            if (color[v] == -1) {
                color[v] = color[u] ^ 1;
                if (color[v] == 0) a++;
                else b++;
                q.push(v);
            } else if (color[v] == color[u]) {
                bipart = false;
            }
        }
    }

    return bipart;
}

