#include <bits/stdc++.h>
#include "tickets.h"
#include <vector>
#include <queue>
#include <algorithm>
#include <tuple>
using namespace std;

long long find_maximum(int k, vector<vector<int>> x) {
    int n = x.size();
    int m = x[0].size();

    vector<int> u(n, 0);
    vector<vector<int>> rounds_upper(n);

    priority_queue<tuple<long long, int, int>> step_heap;
    for (int i = 0; i < n; ++i) {
        if (k == 0) continue;
        long long mg = 2LL * x[i][k - 1];
        step_heap.push(make_tuple(mg, -i, 0));
    }

    long long sum_marginal = 0;
    int T = (n / 2) * k;
    int selected = 0;
    vector<int> steps_taken(n, 0);

    while (selected < T && !step_heap.empty()) {
        auto top = step_heap.top();
        step_heap.pop();
        long long mg = get<0>(top);
        int i = -get<1>(top);
        int t = get<2>(top);

        if (steps_taken[i] >= k) continue;

        sum_marginal += mg;
        steps_taken[i]++;
        u[i]++;
        selected++;

        if (steps_taken[i] < k) {
            int next_t = steps_taken[i];
            int idx = k - next_t - 1;
            long long new_mg = 2LL * x[i][idx];
            step_heap.push(make_tuple(new_mg, -i, next_t));
        }
    }

    vector<pair<int, int>> colors_order;
    for (int i = 0; i < n; ++i) {
        colors_order.emplace_back(-u[i], i);
    }
    sort(colors_order.begin(), colors_order.end());

    priority_queue<pair<int, int>> round_pq;
    for (int r = 0; r < k; ++r) {
        round_pq.push({n / 2, r});
    }

    for (auto& p : colors_order) {
        int i = p.second;
        int ui = u[i];
        for (int s = 0; s < ui; ++s) {
            if (round_pq.empty()) break;
            auto cap_r = round_pq.top();
            round_pq.pop();
            int cap = cap_r.first;
            int r = cap_r.second;
            rounds_upper[i].push_back(r);
            cap--;
            if (cap > 0) {
                round_pq.push({cap, r});
            }
        }
    }

    vector<vector<int>> s(n, vector<int>(m, -1));

    for (int i = 0; i < n; ++i) {
        sort(rounds_upper[i].begin(), rounds_upper[i].end());
        int ui = u[i];

        for (int pos = 0; pos < ui; ++pos) {
            int r = rounds_upper[i][pos];
            int ticket_j = (k - ui) + pos;
            s[i][ticket_j] = r;
        }

        vector<int> lower_rounds;
        for (int r = 0; r < k; ++r) {
            if (!binary_search(rounds_upper[i].begin(), rounds_upper[i].end(), r)) {
                lower_rounds.push_back(r);
            }
        }
        sort(lower_rounds.begin(), lower_rounds.end());

        for (int pos = 0; pos < (int)lower_rounds.size(); ++pos) {
            int r = lower_rounds[pos];
            s[i][pos] = r;
        }
    }

    allocate_tickets(s);

    long long sum_initial = 0;
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < k; ++j) {
            sum_initial -= x[i][j];
        }
    }
    return sum_initial + sum_marginal;
}

