#include <iostream>
#include <vector>
#include <stack>
#include <algorithm>
using namespace std;

struct Node {
    int depth;
    int max_diam;
    vector<int> children;
};

int compute_diameter(const string& s) {
    stack<int> st;
    st.push(0);
    vector<Node> nodes(1);
    nodes.reserve(s.size() / 2 + 1);
    int current = 0;

    for (char c : s) {
        if (c == '(') {
            int parent = st.top();
            nodes[parent].children.push_back(nodes.size());
            st.push(nodes.size());
            nodes.push_back({0, 0, {}});
            current = nodes.size() - 1;
            nodes[current].depth = 0;
            nodes[current].max_diam = 0;
        } else {
            st.pop();
        }
    }

    int max_diam = 0;

    auto dfs = [&](auto self, int u) -> int {
        int max_depth = 0;
        int second_max_depth = 0;
        for (int v : nodes[u].children) {
            int depth = self(self, v) + 1;
            if (depth > max_depth) {
                second_max_depth = max_depth;
                max_depth = depth;
            } else if (depth > second_max_depth) {
                second_max_depth = depth;
            }
        }
        int diam_through_u = max_depth + second_max_depth;
        max_diam = max(max_diam, max(diam_through_u, nodes[u].max_diam));
        return max_depth;
    };

    dfs(dfs, 0);
    return max_diam;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, q;
    cin >> n >> q;
    string s;
    cin >> s;

    vector<int> results;
    results.reserve(q + 1);

    int current_diameter = compute_diameter(s);
    results.push_back(current_diameter);

    for (int i = 0; i < q; ++i) {
        int a, b;
        cin >> a >> b;
        a--;
        b--;

        if (s[a] == s[b]) {
            if (s[a] == ')') {
                results.push_back(current_diameter);
                continue;
            }
        }

        swap(s[a], s[b]);
        current_diameter = compute_diameter(s);
        results.push_back(current_diameter);
    }

    for (int res : results) {
        cout << res << '\n';
    }

    return 0;
}

