    for (int& num : a) {
        cin >> num;
    }

    vector<pair<int, int>> bad_pairs;
    int max_u = 0, max_v = 0;
    for (int i = 0; i < n - 1; ++i) {
        if (a[i] > a[i + 1]) {
            bad_pairs.emplace_back(a[i], a[i + 1]);
            max_u = max(max_u, a[i]);
            max_v = max(max_v, a[i + 1]);
        }
    }

    if (bad_pairs.empty()) {
        long long total = (1LL * x * (x + 1)) / 2;
        cout << total << '\n';
        return 0;
    }

    vector<int> last_occurrence(x + 2, -1);
    int current_max = -1;
    for (int i = 0; i < n; ++i) {
        if (a[i] <= x) {
            current_max = max(current_max, i);
            last_occurrence[a[i]] = current_max;
        }
    }

    vector<int> prefix_max(x + 2, -1);
    for (int l = 1; l <= x + 1; ++l) {
        prefix_max[l] = max(prefix_max[l - 1], last_occurrence[l - 1]);
    }

    vector<int> first_occurrence(x + 2, n);
    for (int i = n - 1; i >= 0; --i) {
        if (a[i] <= x) {
            first_occurrence[a[i]] = i;
        }
    }

    vector<int> first_pos_r(x + 2, n);
    first_pos_r[x] = first_occurrence[x + 1];
    for (int r = x - 1; r >= 0; --r) {
        first_pos_r[r] = min(first_occurrence[r + 1], first_pos_r[r + 1]);
    }

    long long count = 0;
    int valid_l_start = max_u + 1;
    int valid_r_start = max_v;

    for (int l = valid_l_start; l <= x; ++l) {
        int last_pos = prefix_max[l];
        if (last_pos == -1) {
            int r_min = max(valid_r_start, l);
            if (r_min > x) continue;
            count += x - r_min + 1;
        } else {
            int start_r = max(valid_r_start, l);
            if (start_r > x) continue;
            int low = start_r, high = x, res = x + 1;
            while (low <= high) {
                int mid = (low + high) / 2;
                if (first_pos_r[mid] > last_pos) {
                    res = mid;
                    high = mid - 1;
                } else {
                    low = mid + 1;
                }
            }
            if (res <= x) {
                count += x - res + 1;
            }
        }
    }

    cout << count << '\n';
    return 0;
}

