typedef long long ll;
const int N=2e5+5;

int n,m,p[N],pos[N];
vector<int> d[N];
vector<pair<int,int>> q[N];
ll ans[N],bit[N];

void upd(int x, int v) {
    for(;x<=n;x+=x&-x) bit[x]+=v;
}

ll qry(int x) {
    ll res=0;
    for(;x>0;x-=x&-x) res+=bit[x];
    return res;
}

int main() {
