}

#include <bits/stdc++.h>
using namespace std;

typedef long long ll;

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m;
    cin >> n >> m;
    vector<ll> v(n);
    for (ll& x : v) cin >> x;

    vector<ll> wa_vec(m + 2, 0), wb_vec(m + 2, 0), wf_vec(m + 2, 0), ws_vec(m + 2, 0);

    for (int s = 2; s <= 2 * m; ++s) {
        vector<int> mods(n);
        vector<ll> divs(n);
        for (int i = 0; i < n; ++i) {
            divs[i] = v[i] / s;
            mods[i] = v[i] % s;
        }
        sort(mods.begin(), mods.end());

        int a_min = max(1, s - m);
        int a_max = min(m, s - 1);
        if (a_min > a_max) continue;

        vector<int> cnt(s + 1, 0);
        for (int mod : mods) cnt[mod + 1]++;
        for (int i = 1; i <= s; ++i) cnt[i] += cnt[i - 1];

        for (int a = a_min; a <= a_max; ++a) {
            int b = s - a;
            if (b < 1 || b > m) continue;

            int x_parity = 0;
            for (int i = 0; i < n; ++i) x_parity ^= (2 * divs[i] + (mods[i] >= a)) % 2;
            int y_parity = 0;
            for (int i = 0; i < n; ++i) y_parity ^= (2 * divs[i] + (mods[i] >= b)) % 2;

            if (x_parity && !y_parity) wa_vec[a]++, wa_vec[a+1]--;
            else if (!x_parity && y_parity) wb_vec[b]++, wb_vec[b+1]--;
            else if (x_parity && y_parity) wf_vec[a]++, wf_vec[a+1]--;
            else ws_vec[a]++, ws_vec[a+1]--;
        }
    }

    ll wa = 0, wb = 0, wf = 0, ws = 0;
    for (int a = 1; a <= m; ++a) {
        wa += wa_vec[a];
        wb += wb_vec[a];
        wf += wf_vec[a];
        ws += ws_vec[a];
    }

    cout << wa << ' ' << wb << ' ' << wf << ' ' << ws << '\n';

    return 0;
}

