#include <bits/stdc++.h>
using namespace std;

struct Edge {
    int a, b, e, idx;
    bool operator<(const Edge& other) const {
        return e < other.e;
    }
};

struct DSU {
    vector<int> parent, rank;
    DSU(int n) {
        parent.resize(n+1);
        rank.resize(n+1, 1);
        for (int i = 0; i <= n; i++)
            parent[i] = i;
    }
    int find(int u) {
        if (parent[u] != u)
            parent[u] = find(parent[u]);
        return parent[u];
    }
    bool unite(int u, int v) {
        u = find(u), v = find(v);
        if (u == v) return false;
        if (rank[u] < rank[v])
            swap(u, v);
        parent[v] = u;
        rank[u] += rank[v];
        return true;
    }
};

class LazySegmentTree {
private:
    int n;
    vector<int> tree;
    vector<int> lazy;

public:
    LazySegmentTree(int size) {
        n = 1;
        while (n < size) n <<= 1;
        tree.assign(2 * n, 1e9);
        lazy.assign(2 * n, 1e9);
        for (int i = 0; i < size; i++) {
            tree[n + i] = 1e9;
        }
        for (int i = n - 1; i > 0; i--) {
            tree[i] = min(tree[2*i], tree[2*i+1]);
        }
    }

    void push(int node, int l, int r) {
        if (lazy[node] == 1e9) return;
        tree[node] = min(tree[node], lazy[node]);
        if (l != r) {
            lazy[2*node] = min(lazy[2*node], lazy[node]);
            lazy[2*node+1] = min(lazy[2*node+1], lazy[node]);
        }
        lazy[node] = 1e9;
    }

    void update_range(int node, int l, int r, int ul, int ur, int val) {
        push(node, l, r);
        if (r < ul || l > ur) return;
        if (ul <= l && r <= ur) {
            lazy[node] = min(lazy[node], val);
            push(node, l, r);
            return;
        }
        int mid = (l + r) / 2;
        update_range(2*node, l, mid, ul, ur, val);
        update_range(2*node+1, mid+1, r, ul, ur, val);
        tree[node] = min(tree[2*node], tree[2*node+1]);
    }

    void update(int l, int r, int val) {
        update_range(1, 0, n-1, l, r, val);
    }

    int query_point(int idx) {
        int node = 1, l = 0, r = n-1;
        while (l < r) {
            push(node, l, r);
            int mid = (l + r) / 2;
            if (idx <= mid) {
                node = 2*node;
                r = mid;
            } else {
                node = 2*node+1;
                l = mid+1;
            }
        }
        push(node, l, r);
        return tree[node];
    }
};

class HLDMax {
private:
    int n, root, current_pos;
    vector<int> parent, depth, heavy, head, pos, vertex;
    vector<vector<pair<int, int>>> adj;
    vector<int> edge_weight;
    vector<int> segtree;

    int dfs_size(int u, int p, int d) {
        parent[u] = p;
        depth[u] = d;
        int size = 1;
        int max_c_size = 0;
        for (auto [v, w] : adj[u]) {
            if (v != p) {
                edge_weight[v] = w;
                int c_size = dfs_size(v, u, d+1);
                size += c_size;
                if (c_size > max_c_size) {
                    max_c_size = c_size;
                    heavy[u] = v;
                }
            }
        }
        return size;
    }

    void decompose(int u, int h) {
        head[u] = h;
        pos[u] = current_pos;
        vertex[current_pos++] = u;
        if (heavy[u] != -1) {
            decompose(heavy[u], h);
            for (auto [v, w] : adj[u]) {
                if (v != parent[u] && v != heavy[u]) {
                    decompose(v, v);
                }
            }
        }
    }

    void build_segtree(int node, int start, int end) {
        if (start == end) {
            segtree[node] = edge_weight[vertex[start]];
            return;
        }
        int mid = (start + end) / 2;
        build_segtree(2*node, start, mid);
        build_segtree(2*node+1, mid+1, end);
        segtree[node] = max(segtree[2*node], segtree[2*node+1]);
    }

    int query_segtree(int node, int start, int end, int l, int r) {
        if (r < start || end < l) return 0;
        if (l <= start && end <= r) return segtree[node];
        int mid = (start + end) / 2;
        return max(query_segtree(2*node, start, mid, l, r),
                   query_segtree(2*node+1, mid+1, end, l, r));
    }

public:
    HLDMax(int n, const vector<vector<pair<int, int>>>& adj, int root)
        : n(n), adj(adj), root(root) {
        parent.resize(n+1);
        depth.resize(n+1);
        heavy.assign(n+1, -1);
        head.resize(n+1);
        pos.resize(n+1);
        vertex.resize(n+1);
        edge_weight.resize(n+1);
        current_pos = 0;

        dfs_size(root, -1, 0);
        decompose(root, root);

        segtree.resize(4 * n);
        build_segtree(1, 0, current_pos-1);
    }

    int query_path(int u, int v) {
        int res = 0;
        while (head[u] != head[v]) {
            if (depth[head[u]] < depth[head[v]])
                swap(u, v);
            res = max(res, query_segtree(1, 0, current_pos-1, pos[head[u]], pos[u]));
            u = parent[head[u]];
        }
        if (u == v) return res;
        if (depth[u] > depth[v])
            swap(u, v);
        res = max(res, query_segtree(1, 0, current_pos-1, pos[u]+1, pos[v]));
        return res;
    }
};

class HLDMin {
private:
    int n, root, current_pos;
    vector<int> parent, depth, heavy, head, pos, vertex;
    vector<vector<pair<int, int>>> adj;
    LazySegmentTree segtree;

    int dfs_size(int u, int p, int d) {
        parent[u] = p;
        depth[u] = d;
        int size = 1;
        int max_c_size = 0;
        for (auto [v, w] : adj[u]) {
            if (v != p) {
                int c_size = dfs_size(v, u, d+1);
                size += c_size;
                if (c_size > max_c_size) {
                    max_c_size = c_size;
                    heavy[u] = v;
                }
            }
        }
        return size;
    }

    void decompose(int u, int h) {
        head[u] = h;
        pos[u] = current_pos;
        vertex[current_pos++] = u;
        if (heavy[u] != -1) {
            decompose(heavy[u], h);
            for (auto [v, w] : adj[u]) {
                if (v != parent[u] && v != heavy[u]) {
                    decompose(v, v);
                }
            }
        }
    }

public:
    HLDMin(int n, const vector<vector<pair<int, int>>>& adj, int root)
        : n(n), adj(adj), root(root), segtree(n) {
        parent.resize(n+1);
        depth.resize(n+1);
        heavy.assign(n+1, -1);
        head.resize(n+1);
        pos.resize(n+1);
        vertex.resize(n+1);
        current_pos = 0;

        dfs_size(root, -1, 0);
        decompose(root, root);
    }

    void update_path(int u, int v, int value) {
        while (head[u] != head[v]) {
            if (depth[head[u]] < depth[head[v]])
                swap(u, v);
            segtree.update(pos[head[u]], pos[u], value);
            u = parent[head[u]];
        }
        if (u == v) return;
        if (depth[u] > depth[v])
            swap(u, v);
        segtree.update(pos[u]+1, pos[v], value);
    }

    int query_point(int u) {
        return segtree.query_point(pos[u]);
    }
};

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m;
    cin >> n >> m;
    vector<Edge> edges(m);
    vector<vector<pair<int, int>>> adj(n+1);

    for (int i = 0; i < m; i++) {
        cin >> edges[i].a >> edges[i].b >> edges[i].e;
        edges[i].idx = i;
    }

    sort(edges.begin(), edges.end());
    DSU dsu(n);
    vector<bool> in_mst(m, false);
    vector<vector<pair<int, int>>> mst_adj(n+1);

    for (int i = 0; i < m; i++) {
        int a = edges[i].a, b = edges[i].b, e = edges[i].e;
        if (dsu.unite(a, b)) {
            in_mst[i] = true;
            mst_adj[a].emplace_back(b, e);
            mst_adj[b].emplace_back(a, e);
        }
    }

    vector<int> parent(n+1, -1);
    queue<int> q;
    q.push(1);
    parent[1] = -1;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (auto [v, w] : mst_adj[u]) {
            if (parent[v] == -1 && v != parent[u]) {
                parent[v] = u;
                q.push(v);
            }
        }
    }

    vector<vector<pair<int, int>>> adj_mst(n+1);
    for (int u = 1; u <= n; u++) {
        if (parent[u] != -1) {
            int w = 0;
            for (auto [v, e] : mst_adj[u]) {
                if (v == parent[u]) {
                    w = e;
                    break;
                }
            }
            adj_mst[u].emplace_back(parent[u], w);
            adj_mst[parent[u]].emplace_back(u, w);
        }
    }

    HLDMax hld_max(n, adj_mst, 1);
    HLDMin hld_min(n, adj_mst, 1);

    vector<int> output(m);
    vector<Edge> original_edges = edges;
    sort(original_edges.begin(), original_edges.end(), [](const Edge& a, const Edge& b) {
        return a.idx < b.idx;
    });

    for (int i = 0; i < m; i++) {
        Edge& e = original_edges[i];
        if (!in_mst[e.idx]) {
            int a = e.a, b = e.b;
            int e_max = hld_max.query_path(a, b);
            output[i] = e_max;
            int candidate = max(e.e, e_max);
            hld_min.update_path(a, b, candidate);
        }
    }

    for (int i = 0; i < m; i++) {
        Edge& e = original_edges[i];
        if (in_mst[e.idx]) {
            int a = e.a, b = e.b;
            int child;
            if (parent[a] == b) {
                child = a;
            } else if (parent[b] == a) {
                child = b;
            } else {
                child = a;
            }
            output[i] = hld_min.query_point(child);
        }
    }

    for (int i = 0; i < m; i++) {
        cout << output[i] << '\n';
    }

    return 0;
}

