#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n;
    vector<int> a(n);
    vector<int> pos(n + 1);
    for (int i = 0; i < n; ++i) {
        cin >> a[i];
        pos[a[i]] = i + 1;
    }

    if (n < 2) {
        cout << "0\n";
        return 0;
    }

    vector<bool> sieve(n + 1, true);
    sieve[0] = sieve[1] = false;
    for (int i = 2; i * i <= n; ++i) {
        if (sieve[i]) {
            for (int j = i * i; j <= n; j += i) {
                sieve[j] = false;
            }
        }
    }

    vector<int> largest_prime(n + 1, 0);
    int current_max = 0;
    for (int i = 2; i <= n; ++i) {
        if (sieve[i]) {
            current_max = i;
        }
        largest_prime[i] = current_max;
    }

    vector<pair<int, int>> swaps;

    for (int x = 1; x <= n; ++x) {
        int i = pos[x];
        if (i == x) continue;

        if (i < x) {
            while (i < x) {
                int m = x - i + 1;
                m = min(m, n);  // Ensure m doesn't exceed sieve size
                int p = largest_prime[m];
                if (p < 2) p = 2; // Fallback, though shouldn't happen
                int j = i + p - 1;
                j = min(j, x); // Ensure j does not exceed x

                swaps.emplace_back(i, j);
                swap(a[i - 1], a[j - 1]);
                pos[a[i - 1]] = i;
                pos[a[j - 1]] = j;

                i = j;
            }
        } else {
            while (i > x) {
                int m = i - x + 1;
                m = min(m, n);
                int p = largest_prime[m];
                if (p < 2) p = 2;
                int j = i - (p - 1);
                j = max(j, x);

                swaps.emplace_back(j, i);
                swap(a[j - 1], a[i - 1]);
                pos[a[j - 1]] = j;
                pos[a[i - 1]] = i;

                i = j;
            }
        }
    }

    cout << swaps.size() << "\n";
    for (auto [i, j] : swaps) {
        cout << i << " " << j << "\n";
    }

    return 0;
}

