#include <bits/stdc++.h>
#include <vector>
#include <unordered_set>
#include <algorithm>
#include "biscuits.h"

using namespace std;

long long count_tastiness(long long x, vector<long long> a) {
    long long s_max = 0;
    int k = a.size();
    for (int i = 0; i < k; i++) {
        s_max += a[i] * (1LL << i);
    }

    unordered_set<long long> possible_sums;
    possible_sums.insert(0);

    for (int i = 0; i < k; i++) {
        long long bit = (1LL << i);
        if (a[i] == 0) continue;

        unordered_set<long long> new_sums;
        for (long long s : possible_sums) {
            long long max_add = a[i] * bit;
            for (long long add = 0; add <= max_add; add += bit) {
                long long new_s = s + add;
                if (new_s > s_max) break;
                new_sums.insert(new_s);
            }
        }
        possible_sums = move(new_sums);
    }

    unordered_set<long long> ys;
    for (long long s : possible_sums) {
        if (s % x == 0) {
            ys.insert(s / x);
        }
    }

    return ys.size();
}

