        for (int j = 0; j < m; ++j) {
            if (grid[i][j] == c) {
                if (i < min_row) min_row = i;
                if (i > max_row) max_row = i;
                if (j < min_col) min_col = j;
                if (j > max_col) max_col = j;
            }
        }
