    if (min_col > 0) {
        int col = min_col - 1;
        for (int row = min_row; row <= max_row; ++row) {
            char cell = grid[row][col];
            if (cell != '.' && cell != c) deputies.insert(cell);
        }
    }

    if (max_col < m - 1) {
        int col = max_col + 1;
        for (int row = min_row; row <= max_row; ++row) {
            char cell = grid[row][col];
            if (cell != '.' && cell != c) deputies.insert(cell);
        }
    }

    if (min_row > 0) {
        int row = min_row - 1;
        for (int col = min_col; col <= max_col; ++col) {
