#include "longesttrip.h"

using namespace std;

vector<int> rearrange_end(const vector<int>& path, int a) {
    auto it = find(path.begin(), path.end(), a);
    vector<int> part1(path.begin(), it + 1);
    vector<int> part2(it + 1, path.end());
    reverse(part2.begin(), part2.end());
    part1.insert(part1.end(), part2.begin(), part2.end());
    return part1;
}

vector<int> rearrange_start(const vector<int>& path, int b) {
    auto it = find(path.begin(), path.end(), b);
    vector<int> part1(path.begin(), it + 1);
    reverse(part1.begin(), part1.end());
    vector<int> part2(it + 1, path.end());
    part1.insert(part1.end(), part2.begin(), part2.end());
    return part1;
}

int find_connection_to_B(const vector<int>& pathA, const vector<int>& B) {
    int low = 0, high = pathA.size() - 1;
    while (low < high) {
        int mid = (low + high) / 2;
        vector<int> left(pathA.begin(), pathA.begin() + mid + 1);
        if (are_connected(left, B)) {
            high = mid;
        } else {
            low = mid + 1;
        }
    }
    return pathA[low];
}

int find_connection_to_a(int a, const vector<int>& B) {
    int low = 0, high = B.size() - 1;
    while (low < high) {
        int mid = (low + high) / 2;
        vector<int> left(B.begin(), B.begin() + mid + 1);
        if (are_connected({a}, left)) {
            high = mid;
        } else {
            low = mid + 1;
        }
    }
    return B[low];
}

vector<int> solve(const vector<int>& nodes) {
    if (nodes.size() == 1) return nodes;

    int mid = nodes.size() / 2;
    vector<int> A(nodes.begin(), nodes.begin() + mid);
    vector<int> B(nodes.begin() + mid, nodes.end());

    bool connected = are_connected(A, B);

    vector<int> pathA = solve(A);
    vector<int> pathB = solve(B);

    if (connected) {
        int a = find_connection_to_B(A, B);
        int b = find_connection_to_a(a, B);

        vector<int> newPathA = rearrange_end(pathA, a);
        vector<int> newPathB = rearrange_start(pathB, b);

        newPathA.insert(newPathA.end(), newPathB.begin(), newPathB.end());
        return newPathA;
    } else {
        return pathA.size() > pathB.size() ? pathA : pathB;
    }
}

vector<int> longest_trip(int N, int D) {
    vector<int> nodes(N);
    iota(nodes.begin(), nodes.end(), 0);
    return solve(nodes);
}

#include <iostream>
#include <vector>
#include <unordered_map>
#include <algorithm>

using namespace std;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);
    cout.tie(nullptr);

    int n;
    cin >> n;

    vector<int> a(n);
    for (int i = 0; i < n; ++i) {
        cin >> a[i];
    }

    vector<long long> powers;
    long long p = 2;
    for (int i = 0; i < 31; ++i) {
        powers.push_back(p);
        p <<= 1;
    }

    unordered_map<int, int> freq;
    long long total = 0;

    for (int num : a) {
        long long min_s = max(2LL, static_cast<long long>(num) + 1);
        auto it = lower_bound(powers.begin(), powers.end(), min_s);
        int start = it - powers.begin();

        for (int i = start; i < powers.size(); ++i) {
            int target = powers[i] - num;
            auto fit = freq.find(target);
            if (fit != freq.end()) {
                total += fit->second;
            }
        }
        freq[num]++;
    }

    cout << total << '\n';

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

class FenwickTree {
private:
    vector<long long> tree;
public:
    FenwickTree(int size) : tree(size + 1, 0LL) {}

    void update(int idx, long long delta) {
        for (; idx < (int)tree.size(); idx += idx & -idx) {
            tree[idx] += delta;
        }
    }

    long long query(int idx) {
        long long res = 0;
        for (; idx > 0; idx -= idx & -idx) {
            res += tree[idx];
        }
        return res;
    }
};

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int T;
    cin >> T;
    while (T--) {
        int n, q;
        cin >> n >> q;
        FenwickTree ft(n);
        vector<long long> values(n + 1, 0);

        while (q--) {
            int type;
            cin >> type;
            if (type == 1) {
                int x;
                long long y;
                cin >> x >> y;
                long long delta = y - values[x];
                values[x] = y;
                ft.update(x, delta);
            } else {
                int l, r;
                cin >> l >> r;
                cout << ft.query(r) - ft.query(l - 1) << '\n';
            }
        }
    }

    return 0;
}

#include <iostream>
#include <vector>
#include <unordered_map>
#include <string>
#include <numeric>

using namespace std;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int m;
    cin >> m;
    cin.ignore();

    vector<long long> keys;
    keys.reserve(m);

    for (int i = 0; i < m; ++i) {
        string line;
        getline(cin, line);

        size_t pos_plus = line.find('+');
        size_t pos_close = line.find(')');
        size_t pos_slash = line.find('/');

        int a = stoi(line.substr(1, pos_plus - 1));
        int b = stoi(line.substr(pos_plus + 1, pos_close - (pos_plus + 1)));
        int c = stoi(line.substr(pos_slash + 1));

        int sum = a + b;
        int gcd_val = gcd(sum, c);
        int num = sum / gcd_val;
        int den = c / gcd_val;

        long long key = (static_cast<long long>(num) << 32) | den;
        keys.push_back(key);
    }

    unordered_map<long long, int> freq;
    for (auto key : keys) {
        freq[key]++;
    }

    for (int i = 0; i < m; ++i) {
        if (i > 0) {
            cout << ' ';
        }
        cout << freq[keys[i]];
    }
    cout << '\n';

    return 0;
}

