using namespace std;

vector<int> find_subset_with_half(const vector<int>& subset);

vector<int> complement(const vector<int>& all, const vector<int>& subset) {
    vector<int> res;
    for (int x : all) {
        if (find(subset.begin(), subset.end(), x) == subset.end()) {
            res.push_back(x);
        }
    }
    return res;
}

vector<int> find_subset_with_half(const vector<int>& subset) {
    if (subset.size() <= 1) {
        return subset;
    }

    vector<int> left(subset.begin(), subset.begin() + subset.size()/2);
    vector<int> right(subset.begin() + subset.size()/2, subset.end());

    int res_left = run_diagnostic(left);
    if (res_left == 0) {
        return left;
    }

    int res_right = run_diagnostic(right);
    if (res_right == 0) {
        return right;
    }

    if (res_left == 1) {
        return find_subset_with_half(left);
    } else {
        return find_subset_with_half(right);
    }
}

void process(const vector<int>& subset, vector<int>& result) {
    if (subset.empty()) return;

    vector<int> S = find_subset_with_half(subset);
    if (S.size() == subset.size()) {
        return;
    }

    vector<int> comp = complement(subset, S);
    for (int x : S) {
        result[x] = 1;
    }

    process(comp, result);
}

vector<int> malfunctioning_cores(int N) {
    vector<int> result(N, 0);

    vector<int> candidates;
    for (int i = 0; i < N; ++i) {
        vector<int> T = {i};
        int res = run_diagnostic(T);
        if (res == 0) {
            candidates.push_back(i);
        }
    }

    if (candidates.size() == 2) {
        result[candidates[0]] = 1;
        result[candidates[1]] = 1;
        return result;
    }

    vector<int> all(N);
    iota(all.begin(), all.end(), 0);
    process(all, result);

    return result;
}

