const int MAX_N = 1e6 + 1;

int spf[MAX_N];

void precompute_spf() {
    for (int i = 1; i < MAX_N; ++i) {
        spf[i] = i;
    }
    for (int i = 2; i < MAX_N; ++i) {
        if (spf[i] == i) { // i is a prime
            if ((long long)i * i < MAX_N) {
                for (int j = i * i; j < MAX_N; j += i) {
                    if (spf[j] == j) {
                        spf[j] = i;
                    }
                }
            }
        }
    }
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    precompute_spf();

    int t;
    cin >> t;
    while (t--) {
        int n, m;
        cin >> n >> m;
        if (n == 1) {
            cout << "YES\n";
        } else {
            int d = spf[n];
            if (d <= m) {
                cout << "NO\n";
            } else {
                cout << "YES\n";
            }
        }
    }

    return 0;
}

#include <iostream>
#include <vector>
#include <string>
#include <queue>
#include <climits>
using namespace std;

struct Edge {
    int to, rev, cap, cost;
};

vector<vector<Edge>> graph;
int INF = INT_MAX / 2;

void add_edge(int from, int to, int cap, int cost) {
    graph[from].push_back(Edge{to, (int)graph[to].size(), cap, cost});
    graph[to].push_back(Edge{from, (int)graph[from].size() - 1, 0, -cost});
}

int min_cost_flow(int source, int sink, int max_flow) {
    int n = graph.size();
    vector<int> potential(n, INF);
    potential[source] = 0;

    for (int i = 0; i < n - 1; ++i) {
        bool updated = false;
        for (int v = 0; v < n; ++v) {
            if (potential[v] == INF) continue;
            for (const Edge& e : graph[v]) {
                if (e.cap > 0 && potential[e.to] > potential[v] + e.cost) {
                    potential[e.to] = potential[v] + e.cost;
                    updated = true;
                }
            }
        }
        if (!updated) break;
    }

    int flow = 0;
    int cost = 0;

    while (flow < max_flow) {
        using P = pair<int, int>;
        priority_queue<P, vector<P>, greater<P>> pq;
        vector<int> dist(n, INF);
        vector<int> prev_node(n, -1);
        vector<int> prev_edge(n, -1);

        dist[source] = 0;
        pq.emplace(0, source);

        while (!pq.empty()) {
            auto [d, v] = pq.top();
            pq.pop();

            if (d > dist[v]) continue;

            for (int i = 0; i < graph[v].size(); ++i) {
                const Edge& e = graph[v][i];
                if (e.cap > 0) {
                    int nd = d + e.cost + potential[v] - potential[e.to];
                    if (nd < dist[e.to]) {
                        dist[e.to] = nd;
                        prev_node[e.to] = v;
                        prev_edge[e.to] = i;
                        pq.emplace(nd, e.to);
                    }
                }
            }
        }

        if (dist[sink] == INF) return -1;

        for (int v = 0; v < n; ++v) {
            if (dist[v] != INF) potential[v] += dist[v];
        }

        int min_cap = max_flow - flow;
        for (int v = sink; v != source; v = prev_node[v]) {
            int u = prev_node[v];
            min_cap = min(min_cap, graph[u][prev_edge[v]].cap);
        }

        for (int v = sink; v != source; v = prev_node[v]) {
            int u = prev_node[v];
            Edge& e = graph[u][prev_edge[v]];
            e.cap -= min_cap;
            graph[v][e.rev].cap += min_cap;
            cost += e.cost * min_cap;
        }

        flow += min_cap;
    }

    return cost;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m;
    cin >> n >> m;

    vector<string> s(n);
    for (int i = 0; i < n; ++i) {
        cin >> s[i];
    }

    vector<vector<int>> a(n, vector<int>(m));
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
            cin >> a[i][j];
        }
    }

    int total_right = m * 26;
    int nodes = 1 + n + total_right + 1; // source, left, right, sink
    graph.resize(nodes);

    int source = 0;
    int sink = 1 + n + total_right;

    for (int i = 0; i < n; ++i) {
        add_edge(source, 1 + i, 1, 0);
    }

    for (int j = 0; j < m; ++j) {
        for (int c = 0; c < 26; ++c) {
            int right_node = 1 + n + j * 26 + c;
            add_edge(right_node, sink, 1, 0);
        }
    }

    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
            for (int c = 0; c < 26; ++c) {
                int cost = (s[i][j] == 'a' + c) ? 0 : a[i][j];
                int right_node = 1 + n + j * 26 + c;
                add_edge(1 + i, right_node, 1, cost);
            }
        }
    }

    int result = min_cost_flow(source, sink, n);
    cout << result << '\n';

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

const int MAXN = 1e6 + 5;

int v[MAXN], c[MAXN];
int log2_table[MAXN];
int st_max_v[MAXN][20], st_min_c[MAXN][20];
double prefix_logs[MAXN];

void build_sparse_table(int *arr, int n, int st[][20], bool is_max) {
    for (int i = 0; i < n; ++i)
        st[i][0] = arr[i];
    for (int j = 1; (1 << j) <= n; ++j) {
        for (int i = 0; i + (1 << j) <= n; ++i) {
            if (is_max)
                st[i][j] = max(st[i][j-1], st[i + (1 << (j-1))][j-1]);
            else
                st[i][j] = min(st[i][j-1], st[i + (1 << (j-1))][j-1]);
        }
    }
    log2_table[1] = 0;
    for (int i = 2; i <= n; ++i)
        log2_table[i] = log2_table[i/2] + 1;
}

int query_max(int l, int r) {
    int j = log2_table[r - l + 1];
    return max(st_max_v[l][j], st_max_v[r - (1 << j) + 1][j]);
}

int query_min(int l, int r) {
    int j = log2_table[r - l + 1];
    return min(st_min_c[l][j], st_min_c[r - (1 << j) + 1][j]);
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, k;
    cin >> n >> k;

    for (int i = 0; i < n; ++i) cin >> v[i];
    for (int i = 0; i < n; ++i) cin >> c[i];

    build_sparse_table(v, n, st_max_v, true);
    build_sparse_table(c, n, st_min_c, false);

    vector<int> s(n);
    for (int i = 0; i < n; ++i) {
        int l = i, r = n - 1, j0 = -1;
        while (l <= r) {
            int mid = (l + r) / 2;
            int max_v = query_max(i, mid);
            int min_c = query_min(i, mid);
            if (100 * max_v >= min_c) {
                j0 = mid;
                r = mid - 1;
            } else {
                l = mid + 1;
            }
        }
        if (j0 == -1) {
            s[i] = 100 * query_max(i, n-1);
        } else {
            if (j0 == i) {
                s[i] = min(100 * v[i], c[i]);
            } else {
                int prev_max = query_max(i, j0-1);
                int curr_min = query_min(i, j0);
                s[i] = max(100 * prev_max, curr_min);
            }
        }
    }

    sort(s.begin(), s.end());

    prefix_logs[0] = 0.0;
    for (int i = 1; i <= n; ++i)
        prefix_logs[i] = prefix_logs[i-1] + log(i);

    double expected = 0.0;
    int m = s.size();
    for (int i = 0; i < m; ) {
        int current = s[i];
        int l = i;
        while (i < m && s[i] == current) ++i;
        int r_group = i - 1;

        int a = n - l;
        int b = n - (r_group + 1);

        double prob_a = 0.0;
        if (a >= k) {
            int a_k = a - k;
            if (a_k < 0) a_k = 0;
            double sum_a = prefix_logs[a] - (a_k ? prefix_logs[a_k] : 0.0);
            double sum_denom = prefix_logs[n] - (n >= k ? prefix_logs[n - k] : 0.0);
            prob_a = exp(sum_a - sum_denom);
        }

        double prob_b = 0.0;
        if (b >= k) {
            int b_k = b - k;
            if (b_k < 0) b_k = 0;
            double sum_b = prefix_logs[b] - (b_k ? prefix_logs[b_k] : 0.0);
            double sum_denom = prefix_logs[n] - (n >= k ? prefix_logs[n - k] : 0.0);
            prob_b = exp(sum_b - sum_denom);
        }

        expected += current * (prob_a - prob_b);
    }

    cout << fixed << setprecision(9) << expected << '\n';

    return 0;
}

#include <iostream>
#include <vector>
#include <queue>
#include <deque>
#include <climits>
#include <algorithm>

using namespace std;

const long long INF = LLONG_MAX;

struct Line {
    long long m, b;
    Line(long long m, long long b) : m(m), b(b) {}
    long long eval(long long x) const { return m * x + b; }
};

double intersect(const Line &a, const Line &b) {
    return (double)(b.b - a.b) / (a.m - b.m);
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m, k;
    cin >> n >> m >> k;

    vector<vector<pair<int, long long>>> adj(n + 1);
    for (int i = 0; i < m; ++i) {
        int u, v;
        long long w;
        cin >> u >> v >> w;
        adj[u].emplace_back(v, w);
        adj[v].emplace_back(u, w);
    }

    vector<vector<long long>> dist(n + 1, vector<long long>(k + 1, INF));
    dist[1][0] = 0;

    for (int f = 0; f <= k; ++f) {
        // Process roads for current f using Dijkstra's algorithm
        priority_queue<pair<long long, int>, vector<pair<long long, int>>, greater<>> pq;
        vector<bool> visited(n + 1, false);
        for (int u = 1; u <= n; ++u) {
            if (dist[u][f] != INF) {
                pq.emplace(dist[u][f], u);
            }
        }

        while (!pq.empty()) {
            auto [d, u] = pq.top();
            pq.pop();
            if (visited[u]) continue;
            visited[u] = true;
            for (auto [v, w] : adj[u]) {
                if (dist[v][f] > d + w) {
                    dist[v][f] = d + w;
                    pq.emplace(dist[v][f], v);
                }
            }
        }

        if (f >= k) continue;

        // Collect all u with valid distances and calculate a_u
        vector<int> us;
        vector<long long> a_us;
        for (int u = 1; u <= n; ++u) {
            if (dist[u][f] != INF) {
                us.push_back(u);
                a_us.push_back(dist[u][f] + (long long)u * u);
            }
        }
        if (us.empty()) continue;

        // Build convex hull
        deque<Line> dq;
        for (size_t i = 0; i < us.size(); ++i) {
            int u = us[i];
            long long a_u = a_us[i];
            Line line(-2LL * u, a_u);
            while (dq.size() >= 2) {
                Line l1 = dq.back();
                dq.pop_back();
                Line l2 = dq.back();
                double x1 = intersect(l2, l1);
                double x2 = intersect(l1, line);
                if (x1 >= x2) {
                    // l1 is redundant, keep l2
                } else {
                    dq.push_back(l1);
                    break;
                }
            }
            dq.push_back(line);
        }

        // Query for each v
        for (int v = 1; v <= n; ++v) {
            if (dq.empty()) break;
            long long x = v;
            while (dq.size() >= 2) {
                Line l1 = dq[0];
                Line l2 = dq[1];
                if (l1.eval(x) > l2.eval(x)) {
                    dq.pop_front();
                } else {
                    break;
                }
            }
            long long min_val = dq.front().eval(x) + x * x;
            if (min_val < dist[v][f+1]) {
                dist[v][f+1] = min_val;
            }
        }
    }

    // Compute the minimum time for each city
    for (int i = 1; i <= n; ++i) {
        long long min_dist = INF;
        for (int f = 0; f <= k; ++f) {
            min_dist = min(min_dist, dist[i][f]);
        }
        cout << min_dist << (i == n ? "\n" : " ");
    }

    return 0;
}

#include <bits/stdc++.h>
#include "plants.h"
#include <vector>
#include <unordered_set>
#include <queue>
using namespace std;

int n, k;
vector<unordered_set<int>> adj;

void init(int _k, vector<int> r) {
    k = _k;
    n = r.size();
    adj.resize(n);
    bool changed;
    do {
        changed = false;
        for (int i = 0; i < n; ++i) {
            vector<int> window;
            for (int j = 1; j < k; ++j) {
                int pos = (i + j) % n;
                window.push_back(pos);
            }
            if (window.empty()) continue;
            int required = r[i];
            int definitely_taller = 0;
            int definitely_shorter = 0;
            vector<int> remaining;
            for (int j : window) {
                if (adj[j].count(i)) {
                    definitely_taller++;
                } else if (adj[i].count(j)) {
                    definitely_shorter++;
                } else {
                    remaining.push_back(j);
                }
            }
            int rem = remaining.size();
            int needed = required - definitely_taller;
            if (needed < 0 || needed > rem) continue;
            if (needed == 0) {
                for (int j : remaining) {
                    if (!adj[i].count(j)) {
                        adj[i].insert(j);
                        changed = true;
                    }
                }
            } else if (needed == rem) {
                for (int j : remaining) {
                    if (!adj[j].count(i)) {
                        adj[j].insert(i);
                        changed = true;
                    }
                }
            }
        }
    } while (changed);
}

bool is_reachable(int x, int y) {
    if (x == y) return true;
    vector<bool> visited(n, false);
    queue<int> q;
    q.push(x);
    visited[x] = true;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int v : adj[u]) {
            if (v == y) return true;
            if (!visited[v]) {
                visited[v] = true;
                q.push(v);
            }
        }
    }
    return false;
}

int compare_plants(int x, int y) {
    if (is_reachable(x, y)) {
        return 1;
    } else if (is_reachable(y, x)) {
        return -1;
    } else {
        return 0;
    }
}

#include <iostream>
#include <cmath>
#include <iomanip>

using namespace std;

const double PI = acos(-1.0);

struct Point {
    double x, y, z;
};

double get_angle(double x, double y) {
    double angle = atan2(y, x);
    if (angle < 0) angle += 2 * PI;
    return angle;
}

double get_dist(Point a, Point b) {
    double dx = a.x - b.x;
    double dy = a.y - b.y;
    double dz = a.z - b.z;
    return sqrt(dx * dx + dy * dy + dz * dz);
}

double get_cone_dist(double r, double h, Point a, Point b) {
    double a_rad = sqrt(a.x * a.x + a.y * a.y);
    double b_rad = sqrt(b.x * b.x + b.y * b.y);

    if (a.z == 0 && b.z == 0) {
        double angle_diff = abs(get_angle(a.x, a.y) - get_angle(b.x, b.y));
        angle_diff = min(angle_diff, 2 * PI - angle_diff);
        return min(angle_diff * r, a_rad + b_rad);
    }

    double l = sqrt(r * r + h * h);
    double angle_diff = abs(get_angle(a.x, a.y) - get_angle(b.x, b.y));
    angle_diff = min(angle_diff, 2 * PI - angle_diff);

    double dist1 = sqrt(a_rad * a_rad + a.z * a.z) + sqrt(b_rad * b_rad + b.z * b.z);
    double dist2 = angle_diff * r / l * sqrt((a.z - h) * (a.z - h) + a_rad * a_rad) + sqrt((b.z - h) * (b.z - h) + b_rad * b_rad);
    double dist3 = angle_diff * r / l * sqrt((b.z - h) * (b.z - h) + b_rad * b_rad) + sqrt((a.z - h) * (a.z - h) + a_rad * a_rad);
    double dist4 = sqrt((a_rad - b_rad) * (a_rad - b_rad) + (a.z - b.z) * (a.z - b.z));

    double min_dist = min(dist1, min(dist2, min(dist3, dist4)));

    if (a.z == 0 || b.z == 0) {
        double base_dist = a_rad + b_rad;
        min_dist = min(min_dist, base_dist);
    }

    return min_dist;
}

int main() {
    double r, h;
    cin >> r >> h;

    Point a, b;
    cin >> a.x >> a.y >> a.z;
    cin >> b.x >> b.y >> b.z;

    double dist = get_cone_dist(r, h, a, b);
    cout << fixed << setprecision(10) << dist << endl;

    return 0;
}

#include <bits/stdc++.h>
#include "plants.h"
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

vector<int> pos;
vector<int> rank_to_pos;
int n;

void init(int k, vector<int> r) {
    n = r.size();
    pos.assign(n, -1);
    rank_to_pos.resize(n);

    vector<int> modified_r = r;
    queue<int> q;

    for (int i = 0; i < n; ++i) {
        if (modified_r[i] == 0) {
            q.push(i);
        }
    }

    int current_rank = 0;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        if (pos[u] != -1) continue;

        pos[u] = current_rank;
        rank_to_pos[current_rank] = u;
        current_rank++;

        // Determine the range of plants affected by u's processing
        int start = (u + 1) % n;
        for (int i = 0; i < k-1; ++i) {
            int v = (start + i) % n;
            if (modified_r[v] > 0) {
                modified_r[v]--;
                if (modified_r[v] == 0) {
                    q.push(v);
                }
            }
        }
    }
}

int compare_plants(int x, int y) {
    if (pos[x] < pos[y]) {
        return 1;
    } else {
        return -1;
    }
}

#include <bits/stdc++.h>
using namespace std;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m;
    cin >> n >> m;

    vector<vector<int>> A(n, vector<int>(m));
    vector<vector<int>> B(n, vector<int>(m));

    for (int i = 0; i < n; ++i)
        for (int j = 0; j < m; ++j)
            cin >> A[i][j];

    for (int i = 0; i < n; ++i)
        for (int j = 0; j < m; ++j)
            cin >> B[i][j];

    vector<vector<int>> sortedA = A;
    vector<vector<int>> sortedB = B;

    sort(sortedA.begin(), sortedA.end());
    sort(sortedB.begin(), sortedB.end());

    if (sortedA != sortedB) {
        cout << -1 << endl;
        return 0;
    }

    vector<vector<int>> current = B;
    vector<int> sequence;

    while (current != A) {
        vector<int> sorted_cols;

        for (int c = 0; c < m; ++c) {
            bool sorted = true;
            for (int i = 1; i < n; ++i) {
                if (current[i][c] < current[i-1][c]) {
                    sorted = false;
                    break;
                }
            }
            if (sorted) sorted_cols.push_back(c);
        }

        if (sorted_cols.empty()) {
            cout << -1 << endl;
            return 0;
        }

        bool found = false;

        for (int c : sorted_cols) {
            map<int, vector<vector<int>>> groups_current;
            for (const auto& row : current) {
                groups_current[row[c]].push_back(row);
            }

            map<int, vector<vector<int>>> groups_A;
            for (const auto& row : A) {
                groups_A[row[c]].push_back(row);
            }

            if (groups_current.size() != groups_A.size()) continue;

            bool same_groups = true;
            for (auto& [key, val] : groups_current) {
                if (groups_A.find(key) == groups_A.end() || groups_A[key].size() != val.size()) {
                    same_groups = false;
                    break;
                }
            }
            if (!same_groups) continue;

            bool valid = true;
            for (auto& [key, val] : groups_current) {
                if (groups_A[key] != val) {
                    valid = false;
                    break;
                }
            }
            if (valid) {
                set<int> seen;
                vector<int> a_keys_order;
                for (const auto& row : A) {
                    int key = row[c];
                    if (seen.find(key) == seen.end()) {
                        seen.insert(key);
                        a_keys_order.push_back(key);
                    }
                }

                vector<vector<int>> new_current;
                for (int key : a_keys_order) {
                    new_current.insert(new_current.end(), groups_current[key].begin(), groups_current[key].end());
                }

                current = new_current;
                sequence.push_back(c + 1); // columns are 1-based
                found = true;
                break;
            }
        }

        if (!found) {
            cout << -1 << endl;
            return 0;
        }
    }

    reverse(sequence.begin(), sequence.end());
    cout << sequence.size() << endl;
    for (int c : sequence) {
        cout << c << " ";
    }
    if (!sequence.empty()) cout << endl;

    return 0;
}

