#include <bits/stdc++.h>
using namespace std;

vector<int> computeFailure(const string& virus) {
    int n = virus.size();
    vector<int> fail(n, 0);
    for (int i = 1; i < n; ++i) {
        int j = fail[i-1];
        while (j > 0 && virus[i] != virus[j]) {
            j = fail[j-1];
        }
        if (virus[i] == virus[j]) {
            j++;
        }
        fail[i] = j;
    }
    return fail;
}

vector<vector<int>> computeTrans(const string& virus, const vector<int>& fail) {
    int m = virus.size();
    vector<vector<int>> trans(m, vector<int>(26, 0));
    for (int k = 0; k < m; ++k) {
        for (char c = 'A'; c <= 'Z'; ++c) {
            int current_k = k;
            while (current_k > 0 && virus[current_k] != c) {
                current_k = fail[current_k - 1];
            }
            if (virus[current_k] == c) {
                current_k++;
            }
            trans[k][c - 'A'] = current_k;
        }
    }
    return trans;
}

int main() {
    string s1, s2, virus;
    cin >> s1 >> s2 >> virus;
    int len_s1 = s1.size(), len_s2 = s2.size(), m = virus.size();
    if (m == 0) {
        cout << s1.substr(0, min(len_s1, len_s2)) << endl;
        return 0;
    }
    vector<int> fail = computeFailure(virus);
    vector<vector<int>> trans = computeTrans(virus, fail);

    int dp[101][101][101];
    memset(dp, -1, sizeof(dp));
    dp[0][0][0] = 0;
    tuple<int, int, int> parent[101][101][101];
    char pc[101][101][101] = {};

    for (int i = 0; i <= len_s1; ++i) {
        for (int j = 0; j <= len_s2; ++j) {
            for (int k = 0; k < m; ++k) {
                if (dp[i][j][k] == -1) continue;
                if (i < len_s1 && dp[i+1][j][k] < dp[i][j][k]) {
                    dp[i+1][j][k] = dp[i][j][k];
                    parent[i+1][j][k] = {i, j, k};
                    pc[i+1][j][k] = 0;
                }
                if (j < len_s2 && dp[i][j+1][k] < dp[i][j][k]) {
                    dp[i][j+1][k] = dp[i][j][k];
                    parent[i][j+1][k] = {i, j, k};
                    pc[i][j+1][k] = 0;
                }
                if (i < len_s1 && j < len_s2 && s1[i] == s2[j]) {
                    char c = s1[i];
                    int next_k = trans[k][c - 'A'];
                    if (next_k < m && dp[i+1][j+1][next_k] < dp[i][j][k] + 1) {
                        dp[i+1][j+1][next_k] = dp[i][j][k] + 1;
                        parent[i+1][j+1][next_k] = {i, j, k};
                        pc[i+1][j+1][next_k] = c;
                    }
                }
            }
        }
    }

    int max_len = -1, max_i = 0, max_j = 0, max_k = 0;
    for (int i = 0; i <= len_s1; ++i) {
        for (int j = 0; j <= len_s2; ++j) {
            for (int k = 0; k < m; ++k) {
                if (dp[i][j][k] > max_len) {
                    max_len = dp[i][j][k];
                    max_i = i;
                    max_j = j;
                    max_k = k;
                }
            }
        }
    }

    if (max_len <= 0) {
        cout << "0\n";
        return 0;
    }

    string res;
    int ci = max_i, cj = max_j, ck = max_k;
    while (ci != 0 || cj != 0 || ck != 0) {
        if (pc[ci][cj][ck] != 0) {
            res += pc[ci][cj][ck];
        }
        auto [pi, pj, pk] = parent[ci][cj][ck];
        ci = pi;
        cj = pj;
        ck = pk;
    }
    reverse(res.begin(), res.end());
    cout << res << "\n";
    return 0;
}

