typedef long long ll;

int main() {
    ll n, l;
    cin >> n >> l;
    vector<pair<ll, ll>> start(n);
    for (ll i = 0; i < n; i++) {
        cin >> start[i].first >> start[i].second;
    }

    for (ll i = 0; i < n; i++) {
        vector<vector<ll>> grid(l + 1, vector<ll>(l, 0));
        vector<pair<ll, ll>> q;
        q.emplace_back(start[i].first, start[i].second);
        grid[start[i].second][start[i].first] = i + 1;
        ll head = 0;

        while (head < q.size()) {
            ll x = q[head].first;
            ll y = q[head].second;
            head++;

            ll dx[] = {0, 0, 1, -1};
            ll dy[] = {1, -1, 0, 0};

            for (ll j = 0; j < 4; j++) {
                ll nx = x + dx[j];
                ll ny = y + dy[j];

                if (nx >= 0 && nx < l && ny >= 0 && ny <= l && grid[ny][nx] == 0) {
                    bool conflict = false;
                    for (ll k = 0; k < n; k++) {
                        if (start[k].first == nx && start[k].second == ny) {
                            conflict = true;
                            break;
                        }
                    }
                    if (!conflict) {
                        grid[ny][nx] = i + 1;
                        q.emplace_back(nx, ny);
                    }
                }
            }
        }

        ll count = 0;
        for (ll j = 0; j < l; j++) {
            if (grid[0][j] == i + 1) {
                count++;
            }
        }
        cout << count << '\n';
    }

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

pair<int, int> compute(const vector<int>& arr) {
    int max_sum = 0, current_sum = 0, total = 0;
    for (int num : arr) {
        current_sum += num;
        max_sum = max(max_sum, current_sum);
        total += num;
    }
    return {max_sum, total};
}

int main() {
    ios_base::sync_with_stdio(false);
