        for (int& x : red) cin >> x;
        cin >> m;
        vector<int> blue(m);
        for (int& x : blue) cin >> x;

        auto [sr, tr] = compute(red);
        auto [sb, tb] = compute(blue);

        int ans = max({
            sr,
            sb,
            sr + sb,
            sr + max(0, tb),
            sb + max(0, tr),
            max(0, tr + tb)
        });
        cout << ans << '\n';
    }
}

#include <bits/stdc++.h>
using namespace std;

int count_non_c(int l, int r, char c, const vector<vector<int>>& pre) {
    int idx = c - 'a';
    int cnt_c = pre[idx][r] - (l > 0 ? pre[idx][l-1] : 0);
    return (r - l + 1) - cnt_c;
}

int min_changes(int l, int r, char c, const vector<vector<int>>& pre) {
    if (l == r) {
        return count_non_c(l, r, c, pre);
    }
    int mid = (l + r) / 2;
    int left = min_changes(l, mid, c+1, pre) + count_non_c(mid+1, r, c, pre);
    int right = min_changes(mid+1, r, c+1, pre) + count_non_c(l, mid, c, pre);
    return min(left, right);
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int t;
    cin >> t;
    while (t--) {
        int n;
        string s;
        cin >> n >> s;
        vector<vector<int>> pre(26, vector<int>(n));

        for (int c = 0; c < 26; ++c) {
            char target = 'a' + c;
            pre[c][0] = (s[0] == target);
            for (int i = 1; i < n; ++i) {
                pre[c][i] = pre[c][i-1] + (s[i] == target);
            }
        }

        cout << min_changes(0, n-1, 'a', pre) << '\n';
    }
    return 0;
}

