#include <bits/stdc++.h>
using namespace std;

const int MOD = 998244353;

struct DSU {
    vector<int> parent;
    vector<int> rank;
    vector<int> parity;

    DSU(int n) : parent(n), rank(n, 1), parity(n, 0) {
        iota(parent.begin(), parent.end(), 0);
    }

    pair<int, int> find(int u) {
        if (parent[u] != u) {
            auto [p, w] = find(parent[u]);
            parent[u] = p;
            parity[u] ^= w;
        }
        return {parent[u], parity[u]};
    }

    bool unite(int u, int v, int w) {
        auto [pu, wu] = find(u);
        auto [pv, wv] = find(v);
        if (pu == pv) {
            return (wu ^ wv) == w;
        }
        if (rank[pu] < rank[pv]) {
            swap(pu, pv);
            swap(wu, wv);
        }
        parent[pv] = pu;
        parity[pv] = w ^ wv ^ wu;
        rank[pu] += rank[pv];
        return true;
    }
};

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int r, c;
    cin >> r >> c;
    vector<string> grid(r);
    for (int i = 0; i < r; ++i) {
        cin >> grid[i];
    }

    int nodes = r + c;
    DSU dsu(nodes);

    bool possible = true;
    for (int i = 0; i < r; ++i) {
        for (int j = 0; j < c; ++j) {
            if (grid[i][j] != '?') {
                int val = grid[i][j] - '0';
                int u = i;
                int v = r + j;
                if (!dsu.unite(u, v, val)) {
                    possible = false;
                }
            }
        }
    }

    if (!possible) {
        cout << 0 << '\n';
        return 0;
    }

    unordered_set<int> components;
    for (int i = 0; i < nodes; ++i) {
        auto [root, _] = dsu.find(i);
        components.insert(root);
    }

    int k = components.size();
    int ans = 1;
    for (int i = 0; i < k - 1; ++i) {
        ans = (ans * 2) % MOD;
    }

    cout << ans << '\n';

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

typedef long long ll;
const int MOD = 998244353;
const int MAXN = 705;

ll mod_pow(ll a, ll b) {
    ll res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

ll inv(ll x) {
    return mod_pow(x, MOD-2);
}

ll fact[MAXN], inv_fact[MAXN];
ll comb[MAXN][MAXN];

ll L[MAXN][MAXN], R[MAXN][MAXN];
ll eulerian[MAXN][MAXN];
ll dp_xy[MAXN][MAXN][MAXN];

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);

    int n;
    cin >> n;

    vector<ll> a(n+1), b(n+1), c(n);
    for (int i=1; i<=n; ++i) cin >> a[i];
    for (int i=1; i<=n; ++i) cin >> b[i];
    for (int i=0; i<n; ++i) cin >> c[i];

    // Precompute factorial and inv factorial
    fact[0] = 1;
    for (int i=1; i<MAXN; ++i) fact[i] = fact[i-1] * i % MOD;
    inv_fact[MAXN-1] = inv(fact[MAXN-1]);
    for (int i=MAXN-2; i>=0; --i) inv_fact[i] = inv_fact[i+1] * (i+1) % MOD;

    // Precompute comb numbers
    for (int n=0; n<MAXN; ++n) {
        comb[n][0] = 1;
        for (k=1; k<=n; ++k) {
            comb[n][k] = (comb[n-1][k-1] + comb[n-1][k]) % MOD;
        }
    }

    // Precompute L and R
    L[0][0] = 1;
    R[0][0] = 1;
    for (int m=1; m<=n; ++m) {
        for (int x=1; x<=m; ++x) {
            for (int k=1; k<=m; ++k) {
                int left_size = k-1;
                int right_size = m - k;
                if (x-1 <= left_size) {
                    ll add = comb[m-1][k-1] * L[left_size][x-1] % MOD;
                    add = add * fact[right_size] % MOD;
                    L[m][x] = (L[m][x] + add) % MOD;
                }
            }
        }
        for (int y=1; y<=m; ++y) {
            for (int k=1; k<=m; ++k) {
                int left_size = k-1;
                int right_size = m - k;
                if (y-1 <= right_size) {
                    ll add = comb[m-1][k-1] * fact[left_size] % MOD;
                    add = add * R[right_size][y-1] % MOD;
                    R[m][y] = (R[m][y] + add) % MOD;
                }
            }
        }
    }

    // Precompute Eulerian numbers
    eulerian[0][0] = 1;
    for (int m=1; m<=n; ++m) {
        for (int z=0; z<m; ++z) {
            eulerian[m][z] = ((z+1) * eulerian[m-1][z] % MOD + (m - z) * eulerian[m-1][z-1] % MOD) % MOD;
        }
    }

    // Precompute dp_xy[m][x][y]
    for (int m=1; m<=n; ++m) {
        for (int k=1; k<=m; ++k) {
            int left_size = k-1;
            int right_size = m - k;
            for (int x_left=0; x_left <= left_size; ++x_left) {
                if (L[left_size][x_left] == 0) continue;
                for (int y_right=0; y_right <= right_size; ++y_right) {
                    if (R[right_size][y_right] == 0) continue;
                    ll ways = comb[m-1][k-1];
                    ways = ways * L[left_size][x_left] % MOD;
                    ways = ways * R[right_size][y_right] % MOD;
                    int x = x_left + 1;
                    int y = y_right + 1;
                    dp_xy[m][x][y] = (dp_xy[m][x][y] + ways) % MOD;
                }
            }
        }
    }

    // Compute answers
    for (int m=1; m<=n; ++m) {
        ll ans = 0;
        for (int x=1; x<=m; ++x) {
            for (int y=1; y<=m; ++y) {
                ll cnt_xy = dp_xy[m][x][y];
                if (cnt_xy == 0) continue;
                for (int z=0; z<m; ++z) {
                    ll total = cnt_xy * eulerian[m][z] % MOD;
                    total = total * a[x] % MOD;
                    total = total * b[y] % MOD;
                    total = total * c[z] % MOD;
                    ans = (ans + total) % MOD;
                }
            }
        }
        ans = ans * inv(fact[m]) % MOD; // Adjust for overcounting
        ans = ans * fact[m] % MOD;
        cout << ans << " ";
    }
    cout << endl;

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

typedef long long ll;
typedef pair<ll, ll> Point;

Point operator-(const Point &a, const Point &b) {
    return {a.first - b.first, a.second - b.second};
}

ll cross(const Point &a, const Point &b) {
    return a.first * b.second - a.second * b.first;
}

vector<Point> convexHull(vector<Point> points) {
    if (points.size() <= 1) return points;
    sort(points.begin(), points.end());
    vector<Point> hull;
    for (int phase = 0; phase < 2; ++phase) {
        auto start = hull.size();
        for (auto &point : points) {
            while (hull.size() >= start + 2) {
                Point a = hull[hull.size() - 2];
                Point b = hull.back();
                Point c = point;
                if (cross(b - a, c - a) <= 0) break;
                hull.pop_back();
            }
            hull.push_back(point);
        }
        hull.pop_back();
        reverse(points.begin(), points.end());
    }
    return hull;
}

bool checkSameConvexHull(const vector<Point> &a, const vector<Point> &b) {
    if (a.size() != b.size()) return false;
    int n = a.size();
    for (int i = 0; i < n; ++i) {
        Point da = a[(i + 1) % n] - a[i];
        Point db = b[(i + 1) % n] - b[i];
        if (cross(da, db) != 0) return false;
    }
    return true;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m;
    cin >> n >> m;
    vector<Point> a(n), b(m);
    for (int i = 0; i < n; ++i) {
        cin >> a[i].first >> a[i].second;
    }
    for (int i = 0; i < m; ++i) {
        cin >> b[i].first >> b[i].second;
    }

    auto hullA = convexHull(a);
    auto hullB = convexHull(b);

    if (checkSameConvexHull(hullA, hullB)) {
        cout << "YES\n";
    } else {
        cout << "NO\n";
    }

    return 0;
}

