    cin >> n;
    vector<int> r(n + 1);
    for (int i = 1; i <= n; ++i)
        cin >> r[i];

    vector<vector<int>> adj(n + 1);
    for (int i = 0; i < n - 1; ++i) {
        int a, b;
        cin >> a >> b;
        adj[a].push_back(b);
        adj[b].push_back(a);
    }

    vector<int> max_end(n + 1, 1);
    vector<int> max_start(n + 1, 1);
    int global_max = 1;

    stack<tuple<int, int, bool>> st;
    st.emplace(1, -1, false);

    while (!st.empty()) {
        auto [u, parent, visited] = st.top();
        st.pop();

        if (!visited) {
            st.emplace(u, parent, true);
            for (int v : adj[u]) {
                if (v != parent) {
                    st.emplace(v, u, false);
                }
            }
        } else {
            for (int v : adj[u]) {
                if (v != parent) {
                    if (r[v] < r[u])
                        max_end[u] = max(max_end[u], max_end[v] + 1);
                    if (r[v] > r[u])
                        max_start[u] = max(max_start[u], max_start[v] + 1);
                }
            }
            int candidate = max({max_end[u], max_start[u], max_end[u] + max_start[u] - 1});
            global_max = max(global_max, candidate);
        }
    }

    cout << global_max << '\n';
    return 0;
}

#include <iostream>
#include <set>
using namespace std;

set<long long> s;
bool has_zero = true;

void add(long long x) {
    if (x == 0) has_zero = true;
    else s.insert(x);
}

void remove(long long x) {
    if (x == 0) has_zero = false;
    else s.erase(x);
}

long long query(long long k) {
    if (!has_zero) return 0;

    long long m = 1;
    auto it = s.lower_bound(k);

    while (it != s.end()) {
        long long x = *it;
        if (x % k != 0) {
            ++it;
            continue;
        }

        long long current_m = x / k;
        if (current_m < m) {
            ++it;
            continue;
        }

        if (current_m > m) {
            return m * k;
        }

        ++m;
        ++it;
    }

    return m * k;
}

