        if (op == '+') {
            long long x;
            cin >> x;
            add(x);
        } else if (op == '-') {
            long long x;
            cin >> x;
            remove(x);
        } else {
            long long k;
            cin >> k;
            cout << query(k) << '\n';
        }
    }

    return 0;
}

