using namespace std;

long long max_weights(int N, int M, vector<int> X, vector<int> Y, vector<int> W) {
    vector<vector<pair<int, int>>> catfish(N);
    for (int i = 0; i < M; ++i) {
        catfish[X[i]].emplace_back(Y[i], W[i]);
    }

    long long current_dp[10][10];
    for (int a = 0; a < 10; ++a) {
        for (int b = 0; b < 10; ++b) {
            current_dp[a][b] = LLONG_MIN;
        }
    }
    current_dp[0][0] = 0;

    for (int c = 0; c < N; ++c) {
        long long next_dp[10][10];
        for (int a = 0; a < 10; ++a) {
            for (int b = 0; b < 10; ++b) {
                next_dp[a][b] = LLONG_MIN;
            }
        }

        vector<int> possible_k_next;
        if (c == N - 1) {
            possible_k_next = {0};
        } else {
            possible_k_next.resize(10);
            for (int k = 0; k <= 9; ++k) possible_k_next[k] = k;
        }

        for (int a_prev = 0; a_prev <= 9; ++a_prev) {
            for (int b_prev = 0; b_prev <= 9; ++b_prev) {
                if (current_dp[a_prev][b_prev] == LLONG_MIN) continue;

                long long current_sum = current_dp[a_prev][b_prev];

                for (int k_next : possible_k_next) {
                    long long sum_contribution = 0;
                    for (auto &fish : catfish[c]) {
                        int y = fish.first;
                        int w = fish.second;

                        if (b_prev > y) continue;

                        bool left = a_prev >= (y + 1);
                        bool right = k_next >= (y + 1);

                        if (left || right) {
                            sum_contribution += w;
                        }
                    }

                    long long new_sum = current_sum + sum_contribution;
                    if (new_sum > next_dp[b_prev][k_next]) {
                        next_dp[b_prev][k_next] = new_sum;
                    }
                }
            }
        }

        memcpy(current_dp, next_dp, sizeof(current_dp));
    }

    long long max_sum = 0;
    for (int a = 0; a <= 9; ++a) {
        for (int b = 0; b <= 9; ++b) {
            if (current_dp[a][b] > max_sum) {
                max_sum = current_dp[a][b];
            }
        }
    }

    return max_sum;
}

