#include <bits/stdc++.h>
using namespace std;

typedef long long ll;

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m, MOD;
    cin >> n >> m >> MOD;
    vector<string> grid(n);
    for (int i = 0; i < n; ++i) {
        cin >> grid[i];
    }

    ll result = 1;

    int perimeter = 2 * (n + m);
    vector<pair<int, int>> cells;

    int i = 0, j = 0;
    for (j = 0; j < m; ++j) cells.emplace_back(0, j);
    for (i = 1; i < n; ++i) cells.emplace_back(i, m-1);
    for (j = m-2; j >= 0; --j) cells.emplace_back(n-1, j);
    for (i = n-2; i >= 1; --i) cells.emplace_back(i, 0);

    vector<int> dirs;
    for (int j = 0; j < m; ++j) dirs.push_back(3);
    for (int i = 1; i < n; ++i) dirs.push_back(0);
    for (int j = m-2; j >= 0; --j) dirs.push_back(1);
    for (int i = n-2; i >= 1; --i) dirs.push_back(2);

    int len = cells.size();
    vector<bool> visited(len, false);
    int current = 0;

    while (current < len) {
        if (visited[current]) {
            current++;
            continue;
        }
        int start = current;
        vector<int> path;
        while (!visited[current]) {
            visited[current] = true;
            path.push_back(current);
            auto [i, j] = cells[current];
            int dir = dirs[current];
            char c = grid[i][j];
            int next_dir;
            if (dir == 0) {
                if (c == '/') next_dir = 3;
                else if (c == '\\') next_dir = 1;
                else next_dir = -1;
            } else if (dir == 1) {
                if (c == '/') next_dir = 2;
                else if (c == '\\') next_dir = 0;
                else next_dir = -1;
            } else if (dir == 2) {
                if (c == '/') next_dir = 1;
                else if (c == '\\') next_dir = 3;
                else next_dir = -1;
            } else {
                if (c == '/') next_dir = 0;
                else if (c == '\\') next_dir = 2;
                else next_dir = -1;
            }

            if (next_dir == -1) break;

            int next_pos = -1;
            for (int k = 0; k < len; ++k) {
                auto [ni, nj] = cells[k];
                if (dirs[k] == (next_dir ^ 2)) {
                    if (next_dir == 0 && ni == i && nj == j - 1) next_pos = k;
                    else if (next_dir == 1 && ni == i + 1 && nj == j) next_pos = k;
                    else if (next_dir == 2 && ni == i && nj == j + 1) next_pos = k;
                    else if (next_dir == 3 && ni == i - 1 && nj == j) next_pos = k;
                }
            }
            if (next_pos == -1) break;
            current = next_pos;
        }

        char required = 0;
        bool valid = true;
        int cnt_star = 0;
        for (int pos : path) {
            auto [i, j] = cells[pos];
            char c = grid[i][j];
            int dir = dirs[pos];
            if (c != '*') {
                char exp_c;
                int next_dir;
                if (dir == 0) {
                    next_dir = (required == '/') ? 3 : 1;
                } else if (dir == 1) {
                    next_dir = (required == '/') ? 2 : 0;
                } else if (dir == 2) {
                    next_dir = (required == '/') ? 1 : 3;
                } else {
                    next_dir = (required == '/') ? 0 : 2;
                }
                // Check if the current cell's mirror type matches the required transition
                if (required == 0) {
                    required = c;
                } else {
                    if (c != required) {
                        valid = false;
                        break;
                    }
                }
            } else {
                cnt_star++;
            }
        }

        if (!valid) {
            cout << 0 << endl;
            return 0;
        }

        if (required == 0 && cnt_star > 0) {
            result = (result * 2) % MOD;
        }
    }

    int internal_star = 0;
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
            if (grid[i][j] == '*' && (i > 0 && i < n-1 && j > 0 && j < m-1)) {
                internal_star++;
            }
        }
    }

    ll pow2 = 1;
    for (int i = 0; i < internal_star; ++i) {
        pow2 = (pow2 * 2) % MOD;
    }
    result = (result * pow2) % MOD;

    cout << result << endl;

    return 0;
}

#include <bits/stdc++.h>
