    for (int c = 0; c < 26; ++c) {
        auto& lv = left_chars[c];
        auto& rv = right_chars[c];
        int k = min(lv.size(), rv.size());
        for (int i = 0; i < k; ++i) {
            pairs.emplace_back(lv[i], rv[i]);
        }
    }

    vector<int> left_non_question, right_non_question;
    for (int c = 0; c < 26; ++c) {
        auto& lv = left_chars[c];
        int k = min(lv.size(), right_chars[c].size());
        for (size_t i = k; i < lv.size(); ++i) {
            left_non_question.push_back(lv[i]);
        }
        auto& rv = right_chars[c];
        for (size_t i = k; i < rv.size(); ++i) {
            right_non_question.push_back(rv[i]);
        }
    }

    // Pair left non-? with right ?
    int x = min(left_non_question.size(), right_q.size());
    for (int i = 0; i < x; ++i) {
        pairs.emplace_back(left_non_question[i], right_q[i]);
    }

    // Pair right non-? with left ?
    int y = min(right_non_question.size(), left_q.size());
    for (int i = 0; i < y; ++i) {
        pairs.emplace_back(left_q[i], right_non_question[i]);
    }

    // Pair remaining ? with ?
    int rem_left = left_q.size() - y;
    int rem_right = right_q.size() - x;
    int z = min(rem_left, rem_right);
    for (int i = 0; i < z; ++i) {
        pairs.emplace_back(left_q[y + i], right_q[x + i]);
    }

    cout << pairs.size() << '\n';
    for (auto& p : pairs) {
        cout << p.first << ' ' << p.second << '\n';
    }

    return 0;
}

