typedef long long ll;
const ll INF = LLONG_MAX;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int t;
    cin >> t;
    while (t--) {
        int n, m;
        cin >> n >> m;
        vector<vector<pair<int, int>>> adj(n + 1);
        for (int i = 0; i < m; ++i) {
            int u, v, w;
            cin >> u >> v >> w;
            adj[u].emplace_back(v, w);
            adj[v].emplace_back(u, w);
        }

        vector<int> s(n);
        for (int i = 0; i < n; ++i) {
            cin >> s[i];
        }

        vector<vector<ll>> dist(n + 1, vector<ll>(1001, INF));
        priority_queue<tuple<ll, int, int>, vector<tuple<ll, int, int>>, greater<>> pq;

        int start_s = s[0];
        dist[1][start_s] = 0;
        pq.emplace(0, 1, start_s);

        ll result = -1;
        while (!pq.empty()) {
            auto [time, u, slow] = pq.top();
            pq.pop();

            if (u == n) {
                result = time;
                break;
            }

            if (time > dist[u][slow]) continue;

            for (auto [v, w] : adj[u]) {
                ll new_time = time + (ll)w * slow;
                if (new_time < dist[v][slow]) {
                    dist[v][slow] = new_time;
                    pq.emplace(new_time, v, slow);
                }

                int new_slow = min(slow, s[v - 1]);
                if (new_time < dist[v][new_slow]) {
                    dist[v][new_slow] = new_time;
                    pq.emplace(new_time, v, new_slow);
                }
            }
        }

        cout << result << '\n';
    }
    return 0;
}

