vector<int> bfs(int n, int start, const vector<vector<int>>& adj) {
    vector<int> dist(n + 1, -1);
    queue<int> q;
    q.push(start);
    dist[start] = 0;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int v : adj[u]) {
            if (dist[v] == -1) {
                dist[v] = dist[u] + 1;
                q.push(v);
            }
        }
    }
    return dist;
}

void solve() {
    int n, a, b;
    cin >> n >> a >> b;
    vector<vector<int>> adj(n + 1);
    for (int i = 0; i < n - 1; ++i) {
        int x, y;
        cin >> x >> y;
        adj[x].push_back(y);
        adj[y].push_back(x);
    }

    vector<int> da = bfs(n, a, adj);
    vector<int> db = bfs(n, b, adj);

    int ans = 0;
    for (int u = 1; u <= n; ++u) {
        int s = max(da[u], db[u]);
        if ((s - db[u]) % 2 != 0) {
            s += 1;
        }
        ans = max(ans, s);
    }

    cout << ans << '\n';
}

