int main() {
    int N, M;
    cin >> N >> M;
    // For each K in 1..M, compute sum.
    long long max_sum = -1;
    int best_K = M;
    for (int K = 1; K <= M; ++K) {
        long long sum = 0;
        unordered_set<long long> seen;
        for (int j = 1; j < N; ++j) {
            long long y_j = (1LL * j * K - 1) % N + 1;
            if (y_j > j && !seen.count(y_j)) {
                sum += y_j;
                seen.insert(y_j);
            }
        }
        if (sum > max_sum || (sum == max_sum && K < best_K)) {
            max_sum = sum;
            best_K = K;
        }
    }
    cout << best_K << endl;
}

