int main() {
    int n, k;
    cin >> n >> k;
    vector<int> a(n);
    for (auto &x : a) cin >> x;

    int S = *min_element(a.begin(), a.end());
    int max_a = *max_element(a.begin(), a.end());
    if (max_a - S > k) {
        cout << "NO\n";
        return 0;
    }

    int total_extra = 0;
    for (int x : a) total_extra += x - S;
    if (total_extra > k * (n - 1)) {
        cout << "NO\n";
        return 0;
    }

    vector<int> rem(k + 1, 0);
    int current = 0;
    for (int c = 1; c <= k; ++c) {
        if (current >= total_extra) break;
        int take = min(n - 1, total_extra - current);
        rem[c] = take;
        current += take;
    }

    if (current != total_extra) {
        cout << "NO\n";
        return 0;
    }

    vector<vector<int>> extras(n);
    for (int c = 1; c <= k; ++c) {
        if (rem[c] == 0) continue;
        for (int j = 0; j < rem[c]; ++j) {
            bool assigned = false;
            for (int i = 0; i < n; ++i) {
                if (extras[i].size() < a[i] - S && find(extras[i].begin(), extras[i].end(), c) == extras[i].end()) {
                    extras[i].push_back(c);
                    assigned = true;
                    break;
                }
            }
            if (!assigned) {
                cout << "NO\n";
                return 0;
            }
        }
    }

    cout << "YES\n";
    for (int i = 0; i < n; ++i) {
        vector<int> colors;
        for (int j = 0; j < S; ++j) colors.push_back(1);
        bool has_color1 = false;
        for (int c : extras[i]) if (c == 1) { has_color1 = true; break; }
        if (has_color1) colors.push_back(1);
        for (int c : extras[i]) if (c != 1) colors.push_back(c);
        for (size_t j = 0; j < colors.size(); ++j) {
            if (j > 0) cout << ' ';
            cout << colors[j];
        }
        cout << '\n';
    }
}

#include <bits/stdc++.h>
using namespace std;

vector<int> computeLPS(const vector<int>& pattern) {
    int n = pattern.size();
    vector<int> lps(n, 0);
    for (int i = 1, len = 0; i < n; ) {
        if (pattern[i] == pattern[len]) {
            len++;
            lps[i] = len;
            i++;
        } else {
            if (len != 0) {
                len = lps[len-1];
            } else {
                lps[i] = 0;
                i++;
            }
        }
    }
    return lps;
}

int findRotation(const vector<int>& a, const vector<int>& t) {
    int n = a.size();
    if (n != t.size()) return -1;
    if (n == 0) return 0;

    vector<int> aa;
    aa.reserve(2 * n);
    for (int x : a) aa.push_back(x);
    for (int x : a) aa.push_back(x);

    vector<int> lps = computeLPS(t);
    int m = t.size();
    int j = 0;

    for (int i = 0; i < aa.size(); ++i) {
        while (j > 0 && aa[i] != t[j]) {
            j = lps[j-1];
        }
        if (aa[i] == t[j]) {
            j++;
            if (j == m) {
                int start = i - m + 1;
                if (start < n) {
                    return start;
                }
                j = lps[j-1];
            }
        }
    }
    return -1;
}

int getMinShifts(const vector<int>& a, const vector<int>& t) {
    int start = findRotation(a, t);
    if (start == -1) return -1;
    int n = a.size();
    return (n - start) % n;
}

void solve() {
    int n;
    cin >> n;
    vector<int> a(n);
    for (int& x : a) cin >> x;

    vector<int> s = a;
    sort(s.begin(), s.end());
    if (a == s) {
        cout << 0 << '\n';
        return;
    }

    vector<int> rs = s;
    reverse(rs.begin(), rs.end());

    vector<int> reversed_a(a.rbegin(), a.rend());

    vector<int> options;

    // Scenario 1: shifts to sorted
    int s1 = getMinShifts(a, s);
    if (s1 != -1) options.push_back(s1);

    // Scenario 2: reverse once to sorted
    if (reversed_a == s) options.push_back(1);

    // Scenario 3: reverse then shifts to sorted
    int s3 = getMinShifts(reversed_a, s);
    if (s3 != -1) options.push_back(1 + s3);

    // Scenario 4: shifts to reversed sorted then reverse
    int s4 = getMinShifts(a, rs);
    if (s4 != -1) options.push_back(s4 + 1);

    // Scenario 5: reverse then shifts to reversed sorted then reverse again
    int s5 = getMinShifts(reversed_a, rs);
    if (s5 != -1) options.push_back(2 + s5);

    if (options.empty()) {
        cout << -1 << '\n';
    } else {
        cout << *min_element(options.begin(), options.end()) << '\n';
    }
}

