    sort(a.begin(), a.end());

    vector<int> p;
    int current_max = 0;

    for (int x : a) {
        if (x > current_max + 1) {
            cout << "NO\n";
            return 0;
        }
        if (x > current_max) {
            p.push_back(x);
            current_max = x;
        }
    }

    if (current_max < m) {
        cout << "NO\n";
        return 0;
    }

    vector<bool> has(m + 1, false);
    for (int x : a) has[x] = true;

    vector<bool> dp(m + 1, false);
    dp[0] = true;
    for (int pi : p) {
        for (int i = pi; i <= m; ++i) {
            if (dp[i - pi]) dp[i] = true;
        }
    }

    for (int i = 1; i <= m; ++i) {
        if (dp[i] && !has[i]) {
            cout << "NO\n";
            return 0;
        }
    }

    cout << "YES\n" << p.size() << '\n';
    for (int i = 0; i < p.size(); ++i) {
        if (i > 0) cout << ' ';
        cout << p[i];
    }
    cout << '\n';

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

using ll = long long;
const ll INF = 1e18;

int main() {
    int N, X, Y;
    cin >> N >> X >> Y;
    vector<int> C(N);
    for (auto& c : C) cin >> c;

    // Precompute left_run: the maximum run starting at position l
    vector<int> left_run(N+1);
    for (int l = N-1; l >= 0; --l) {
        if (l+1 < N && C[l] == C[l+1])
            left_run[l] = left_run[l+1] + 1;
        else
            left_run[l] = 1;
    }

    // Precompute right_run[r]: the maximum run starting at the top of the right stack (which has r elements)
    vector<int> right_run(N+1, 0);
    for (int r = 1; r <= N; ++r) {
        int color = C[r-1];
        right_run[r] = 1;
        for (int i = r-2; i >= 0; --i) {
            if (C[i] == color)
                right_run[r]++;
            else
                break;
        }
    }

    vector<vector<ll>> dp(N+2, vector<ll>(N+2, INF));
    dp[0][0] = 0;

    for (int l = 0; l <= N; ++l) {
        for (int r = 0; r <= N; ++r) {
            if (dp[l][r] == INF) continue;

            // Current cost
            ll cost = dp[l][r];

            // Pack from left stack
            if (l < N) {
                int k = left_run[l];
                ll new_cost = cost + X;
                int new_l = l + k;
                int new_r = r;
                if (new_l <= N && new_cost < dp[new_l][new_r])
                    dp[new_l][new_r] = new_cost;
            }

            // Pack from right stack
            if (r > 0) {
                int k = right_run[r];
                ll new_cost = cost + X;
                int new_r = r - k;
                int new_l = l;
                if (new_r >= 0 && new_cost < dp[new_l][new_r])
                    dp[new_l][new_r] = new_cost;
            }

            // Move left to right
            if (l < N) {
                ll new_cost = cost + Y;
                int new_l = l + 1;
                int new_r = r + 1;
                if (new_r <= N && new_cost < dp[new_l][new_r])
                    dp[new_l][new_r] = new_cost;
            }

            // Move right to left
            if (r > 0) {
                ll new_cost = cost + Y;
                int new_r = r - 1;
                int new_l = l - 1;
                if (new_l >= 0 && new_cost < dp[new_l][new_r])
                    dp[new_l][new_r] = new_cost;
            }
        }
    }

    ll ans = INF;
    for (int r = 0; r <= N; ++r) {
        ans = min(ans, dp[N][r] + X * (r > 0 ? 1 : 0));
    }
    cout << ans << endl;
}

#include <bits/stdc++.h>
#include "supertrees.h"
#include <vector>
#include <numeric>

using namespace std;

int construct(vector<vector<int>> p) {
    int n = p.size();
    vector<int> parent(n);
    iota(parent.begin(), parent.end(), 0);

    // DSU find function with path compression
    function<int(int)> find = [&](int u) {
        if (parent[u] != u) {
            parent[u] = find(parent[u]);
        }
        return parent[u];
    };

    // Union all nodes i and j where p[i][j] > 0
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < n; ++j) {
            if (p[i][j] == 0) continue;
            int u = find(i);
            int v = find(j);
            if (u != v) {
                parent[u] = v;
            }
        }
    }

    // Check that the component structure is valid
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < n; ++j) {
            if (p[i][j] == 0 && find(i) == find(j)) {
                return 0;
            }
            if (p[i][j] != 0 && find(i) != find(j)) {
                return 0;
            }
        }
    }

    vector<vector<int>> answer(n, vector<int>(n, 0));

    // Process each component
    for (int i = 0; i < n; ++i) {
        if (find(i) != i) continue; // Skip if not the root of the component
        vector<int> component;
        for (int j = 0; j < n; ++j) {
            if (find(j) == i) {
                component.push_back(j);
            }
        }
        int m = component.size();
        if (m == 1) {
            continue; // Singleton component, no edges needed
        }

        bool all_one = true, all_two = true, all_three = true;
        for (int a : component) {
            for (int b : component) {
                if (a == b) continue;
                if (p[a][b] != 1) all_one = false;
                if (p[a][b] != 2) all_two = false;
                if (p[a][b] != 3) all_three = false;
            }
        }

        if (all_one) {
            // Build a tree (star-shaped)
            int root = component[0];
            for (int j = 1; j < m; ++j) {
                answer[root][component[j]] = 1;
                answer[component[j]][root] = 1;
            }
        } else if (all_two) {
            // Build a cycle
            for (int j = 0; j < m; ++j) {
                int next = (j + 1) % m;
                answer[component[j]][component[next]] = 1;
                answer[component[next]][component[j]] = 1;
            }
        } else if (all_three) {
            // Impossible case
            return 0;
        } else {
            // Mixed cases are invalid
            return 0;
        }
    }

    build(answer);
    return 1;
}

#include <bits/stdc++.h>
using namespace std;
using ll = long long;

ll gcd(ll a, ll b) {
    while (b) { a %= b; swap(a, b); }
    return a;
}

vector<ll> divisors(ll d) {
    vector<ll> res;
    for (ll i = 1; i * i <= d; ++i) {
        if (d % i == 0) {
            res.push_back(i);
            if (i != d / i) res.push_back(d / i);
        }
    }
    sort(res.rbegin(), res.rend());
    return res;
}

bool is_three_squares(ll n) {
    while (n % 4 == 0) n /= 4;
    return n % 8 != 7;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);

    int n;
    cin >> n;
    vector<tuple<ll, ll, ll>> pts(n);
    ll d = 0;
    for (auto& [x, y, z] : pts) {
        cin >> x >> y >> z;
        ll s = x * x + y * y + z * z;
        d = gcd(d, s);
    }

    for (ll r2 : divisors(d)) {
        if (!is_three_squares(r2)) continue;

        vector<tuple<ll, ll, ll>> rs;
        if (r2 == 1) {
            rs = {{1,0,0}, {0,1,0}, {0,0,1}};
        } else if (r2 == 9) {
            rs = {{-1,2,2}, {2,-1,2}, {2,2,-1}};
        } else {
            continue;
        }

        bool ok = true;
        for (auto [x, y, z] : pts) {
            for (auto [a, b, c] : rs) {
                ll dot = x * a + y * b + z * c;
                if (dot % r2 != 0) {
                    ok = false;
                    break;
                }
            }
            if (!ok) break;
        }
        if (ok) {
            cout << r2 << '\n';
            for (auto [a, b, c] : rs) {
                cout << a << ' ' << b << ' ' << c << '\n';
            }
            return 0;
        }
    }

    return 0;
}

#include <bits/stdc++.h>

using namespace std;

void solve() {
    int n;
    cin >> n;
    vector<vector<int>> adj(n + 1);
    for (int i = 0; i < n - 1; ++i) {
        int u, v;
        cin >> u >> v;
        adj[u].push_back(v);
        adj[v].push_back(u);
    }

    vector<int> parent(n + 1);
    vector<vector<int>> children(n + 1);
    vector<bool> visited(n + 1);
    queue<int> q;
    q.push(1);
    parent[1] = 0;
    visited[1] = true;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int v : adj[u]) {
            if (!visited[v]) {
                visited[v] = true;
                parent[v] = u;
                children[u].push_back(v);
                q.push(v);
            }
        }
    }

    int initial_leaves = 0;
    for (int i = 2; i <= n; ++i) {
        if (children[i].empty()) initial_leaves++;
    }

    stack<pair<int, bool>> st;
    st.push({1, false});
    vector<int> post_order;
    while (!st.empty()) {
        auto [u, vis] = st.top();
        st.pop();
        if (vis) {
            post_order.push_back(u);
        } else {
            st.push({u, true});
            for (auto it = children[u].rbegin(); it != children[u].rend(); ++it)
                st.push({*it, false});
        }
    }

    queue<int> q_buds;
    for (int u : post_order) {
        if (u == 1) continue;
        if (children[u].empty()) continue;
        bool is_bud = true;
        for (int v : children[u]) {
            if (!children[v].empty()) {
                is_bud = false;
                break;
            }
        }
        if (is_bud) q_buds.push(u);
    }

    vector<bool> processed(n + 1);
    int count = 0;
    while (!q_buds.empty()) {
        int u = q_buds.front();
        q_buds.pop();
        if (u == 1 || processed[u]) continue;

        bool is_bud = true;
        if (children[u].empty()) is_bud = false;
        else for (int v : children[u]) if (!children[v].empty()) is_bud = false;
        if (!is_bud) continue;

        count++;
        processed[u] = true;
        int p = parent[u];
        auto& c = children[p];
        auto it = find(c.begin(), c.end(), u);
        if (it != c.end()) {
            c.erase(it);
            if (p != 1 && !children[p].empty()) {
                bool p_bud = true;
                for (int v : children[p]) if (!children[v].empty()) p_bud = false;
                if (p_bud) q_buds.push(p);
            }
        }
    }

    cout << max(1, initial_leaves - count) << '\n';
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
    int t;
    cin >> t;
    while (t--) solve();
    return 0;
}

#include <bits/stdc++.h>
using namespace std;

vector<int> a;
vector<vector<int>> children;
vector<int> x;

int compute_x(int u) {
    int current = a[u];
    for (int v : children[u]) {
        current = gcd(current, compute_x(v));
    }
    x[u] = current;
    return current;
}

void multiply_subtree(int u, int val, vector<int>& arr) {
    arr[u] *= val;
    for (int v : children[u]) {
        multiply_subtree(v, val, arr);
    }
}

int main() {
