    cin.tie(0);

    int t;
    cin >> t;
    while (t--) {
        int n, k;
        cin >> n >> k;
        a.resize(n);
        for (int i = 0; i < n; ++i) {
            cin >> a[i];
        }
        children.assign(n, {});
        for (int i = 0; i < n-1; ++i) {
            int u, v;
            cin >> u >> v;
            u--; v--;
            if (u != 0 && v == 0) swap(u, v);
            if (u == 0) {
                children[0].push_back(v);
            } else {
                children[u].push_back(v);
            }
        }
        x.assign(n, 0);
        compute_x(0);
        vector<pair<int, int>> candidates;
        for (int i = 1; i < n; ++i) {
            candidates.emplace_back(-x[i], i);
        }
        sort(candidates.begin(), candidates.end());
        vector<int> current_a = a;
        int m = min(k-1, (int)candidates.size());
        for (int i = 0; i < m; ++i) {
            int u = candidates[i].second;
            multiply_subtree(u, x[u], current_a);
        }
        int g = current_a[0];
        for (int i = 1; i < n; ++i) {
            g = gcd(g, current_a[i]);
        }
        if (k > 0) {
            g = gcd(g, current_a[0]);
            cout << current_a[0] * g << '\n';
        } else {
            cout << current_a[0] << '\n';
        }
    }
    return 0;
}

