#include <bits/stdc++.h>
using namespace std;

struct Node {
    int id;
    Node *c[2], *p;
    int flip;
    int val;
    int path;
    Node(int id) : id(id), flip(0), val(0), path(0) {
        c[0] = c[1] = p = nullptr;
    }
    void update() {
        path = val;
        if (c[0]) path ^= c[0]->path;
        if (c[1]) path ^= c[1]->path;
    }
    void push() {
        if (flip) {
            swap(c[0], c[1]);
            if (c[0]) c[0]->flip ^= 1;
            if (c[1]) c[1]->flip ^= 1;
            flip = 0;
        }
    }
    int dir() {
        if (p && p->c[0] == this) return 0;
        if (p && p->c[1] == this) return 1;
        return -1;
    }
    void rot(int d) {
        auto old = c[d];
        if (old->c[d^1]) old->c[d^1]->p = this;
        c[d] = old->c[d^1];
        int g = dir();
        if (g != -1) p->c[g] = old;
        old->p = p;
        old->c[d^1] = this;
        p = old;
        update();
        old->update();
    }
    void splay() {
        while (dir() != -1) {
            Node *curr = p;
            int d = dir();
            if (curr->dir() == -1) {
                curr->rot(d);
            } else if (curr->dir() == d) {
                curr->p->rot(d);
                curr->rot(d);
            } else {
                curr->rot(d);
                curr->p->rot(d);
            }
        }
        update();
    }
    Node* access() {
        Node *u = this, *v = nullptr;
        for (; u; v = u, u = u->p) {
            u->splay();
            u->c[1] = v;
            u->update();
        }
        splay();
        return v;
    }
    void make_root() {
        access();
        flip ^= 1;
        push();
    }
    friend void link(Node *u, Node *v, int w) {
        u->make_root();
        u->p = v;
        u->val = w;
        u->update();
    }
    friend void cut(Node *u) {
        u->access();
        if (u->c[0]) {
            u->c[0]->p = nullptr;
            u->c[0] = nullptr;
            u->update();
        }
    }
    friend int query_xor(Node *u, Node *v) {
        u->make_root();
        v->access();
        return v->path;
    }
    friend bool connected(Node *u, Node *v) {
        u->access();
        v->access();
        return u->p != nullptr;
    }
};

const int MAXB = 30;
struct Basis {
    int basis[MAXB];
    Basis() { memset(basis, 0, sizeof basis); }
    void insert(int x) {
        for (int i = MAXB-1; i >= 0; --i) {
            if (x & (1<<i)) {
                if (!basis[i]) {
                    basis[i] = x;
                    return;
                }
                x ^= basis[i];
            }
        }
    }
    int minimize(int x) {
        for (int i = MAXB-1; i >= 0; --i) {
            if ((x ^ basis[i]) < x) {
                x ^= basis[i];
            }
        }
        return x;
    }
} basis;

