#include <bits/stdc++.h>
using namespace std;
using ll = long long;

struct Run {
    char c;
    int cnt;
};

ll calculate(vector<Run> runs, int a, int b, int c, bool start_even) {
    vector<int> zeros, ones;
    for (auto& r : runs) {
        if (r.c == '0') zeros.push_back(r.cnt);
        else ones.push_back(r.cnt);
    }
    ll profit = 0;
    int parity = start_even ? 0 : 1;
    int i0 = 0, i1 = 0;
    while (true) {
        bool action = false;
        if (parity == 0) { // even: operation 2
            while (i1 < ones.size()) {
                if (ones[i1] >= 2) {
                    ones[i1]--;
                    profit += b;
                    action = true;
                    break;
                } else {
                    i1++;
                }
            }
        } else { // odd: operation 1 or 3
            while (i0 < zeros.size()) {
                if (zeros[i0] >= 2) {
                    zeros[i0]--;
                    profit += a;
                    action = true;
                    break;
                } else if (zeros[i0] >= 1) {
                    zeros[i0]--;
                    profit -= c;
                    action = true;
                    break;
                } else {
                    i0++;
                }
            }
        }
        if (!action) break;
        parity ^= 1;
    }
    return profit;
}

