        vector<Run> runs;
        char prev = s[0];
        int cnt = 1;
        for (int i = 1; i < n; ++i) {
            if (s[i] == prev) {
                cnt++;
            } else {
                runs.push_back({prev, cnt});
                prev = s[i];
                cnt = 1;
            }
        }
        runs.push_back({prev, cnt});
        ll ans = max(calculate(runs, a, b, c, true), calculate(runs, a, b, c, false));
        cout << ans << '\n';
    }
    return 0;
}

