int main() {
    int n;
    cin >> n;

    vector<int> dist(n + 1, -1);
    queue<int> q;

    dist[n] = 0;
    q.emplace(n);

    while (!q.empty()) {
        int u = q.front();
        q.pop();

        if (u == 1) {
            cout << dist[1] << '\n';
            return 0;
        }

        int row = (int)(sqrt(2 * u) - 1e-9);
        while ((row + 1) * (row + 2) / 2 < u) {
            row++;
        }

        int offset = u - row * (row + 1) / 2;

        int up1 = u - row;
        int up2 = u - row + 1;

        if (up1 >= 1) {
            if (dist[up1] == -1) {
                dist[up1] = dist[u] + 1;
                q.emplace(up1);
            }
        }
          if (up2 >= 1 && offset != 1) {
            if (dist[up2] == -1) {
                dist[up2] = dist[u] + 1;
                q.emplace(up2);
            }
        }
        int down1 = u + row;
        int down2 = u + row + 1;
         if (down1 <= n) {
            if (dist[down1] == -1) {
                dist[down1] = dist[u] + 1;
                q.emplace(down1);
            }
        }
         if (down2 <= n && offset != row+1) {
            if (dist[down2] == -1) {
                dist[down2] = dist[u] + 1;
                q.emplace(down2);
            }
        }
    }

    return 0;
}

