void solve() {
    int n, m;
    cin >> n >> m;

    vector<vector<int>> adj(n + 1);
    for (int i = 0; i < m; ++i) {
        int a, b;
        cin >> a >> b;
        adj[a].push_back(b);
    }

    vector<double> dp(n + 1, 0.0);
    dp[n] = 1.0;

    for (int u = n - 1; u >= 1; --u) {
        if (adj[u].empty()) {
            dp[u] = 0.0;
            continue;
        }

        unordered_map<int, int> cnt;
        for (int v : adj[u]) {
            cnt[v]++;
        }

        double max_val = 0.0;
        int s_total = adj[u].size();

        for (auto &[v, s_i] : cnt) {
            double current = s_i * dp[v];
            if (current > max_val) {
                max_val = current;
            }
        }

        dp[u] = max_val / s_total;
    }

    cout << fixed << setprecision(12) << dp[1] << '\n';
}

