                ans = min(ans, (n - 0 > 0 ? (n - 0) : 0) * a[spell1_idx]);
                for(ll j = 0; j < k; ++j) {
                    if(cost + d[j] <= s) {
                        ans = min(ans, (n - c[j] > 0 ? (n - c[j]) : 0) * a[spell1_idx]);
                    }
                }
            }
        } else if (spell2_idx != -1) {
