int main() {
    int W, H, L;
    cin >> W >> H >> L;

    vector<vector<vector<int>>> dungeon(L, vector<vector<int>>(H, vector<int>(W)));
    for (int k = 0; k < L; ++k) {
        for (int j = 0; j < H; ++j) {
            for (int i = 0; i < W; ++i) {
                cin >> dungeon[k][j][i];
            }
        }
    }

    vector<vector<vector<int>>> dp(L, vector<vector<int>>(H, vector<int>>(W, -1)));
    dp[0][0][0] = dungeon[0][0][0];

    int dx[] = {0, 0, 1, -1, 0, 0}; // Right, Left
    int dy[] = {1, -1, 0, 0, 0, 0}; // Down, Up
    int dz[] = {0, 0, 0, 0, 1, -1}; // Back, Front

    function<int(int, int, int)> solve = [&](int x, int y, int z) {
        if (x < 0 || x >= W || y < 0 || y >= H || z < 0 || z >= L) {
            return -1;
        }
        if (dp[z][y][x] != -1) {
            return dp[z][y][x];
        }

        int max_score = -1;
        for (int i = 0; i < 6; ++i) {
            int nx = x + dx[i];
            int ny = y + dy[i];
            int nz = z + dz[i];

            if (nx >= 0 && nx < W && ny >= 0 && ny < H && nz >= 0 && nz < L) {
                int next_score = solve(nx, ny, nz);
                if (next_score != -1) {
                    max_score = max(max_score, next_score);
                }
            }
        }

        if (max_score == -1) {
            return dp[z][y][x] = -1;
        }

        return dp[z][y][x] = max_score + dungeon[z][y][x];
    };


    function<int(int, int, int)> solve2 = [&](int x, int y, int z) {
        if (x < 0 || x >= W || y < 0 || y >= H || z < 0 || z >= L) {
            return -1;
        }


        int &ret = dp[z][y][x];
        if (ret != -1) return ret;

        ret = -1;
        for(int i = 0; i < 6; ++i){
            int nx = x + dx[i];
            int ny = y + dy[i];
            int nz = z + dz[i];

            if(nx >= 0 && nx < W && ny >= 0 && ny < H && nz >= 0 && nz < L){
                int temp = solve2(nx, ny, nz);
                if(temp != -1){
                    ret = max(ret, temp);
                }
            }
        }

        if(ret != -1) ret += dungeon[z][y][x];
        else ret = -1;

        return ret;
    };


    for (int k = 0; k < L; ++k) {
        for (int j = 0; j < H; ++j) {
            for (int i = 0; i < W; ++i) {
                dp[k][j][i] = -1;
            }
        }
    }
    dp[0][0][0] = dungeon[0][0][0];

    int result = solve2(W - 1, H - 1, L - 1);
    cout << result << endl;

    return 0;
}

