#include <bits/stdc++.h>
using namespace std;

vector<vector<int>> computeDistances(int n, const vector<vector<int>>& adj) {
    vector<vector<int>> dist(n+1, vector<int>(n+1, -1));
    for (int u = 1; u <= n; ++u) {
        queue<int> q;
        q.push(u);
        dist[u][u] = 0;
        while (!q.empty()) {
            int v = q.front();
            q.pop();
            for (int neighbor : adj[v]) {
                if (dist[u][neighbor] == -1) {
                    dist[u][neighbor] = dist[u][v] + 1;
                    q.push(neighbor);
                }
            }
        }
    }
    return dist;
}

vector<int> getMaxDistanceNodes(int x, const vector<vector<int>>& dist, int n) {
    int max_dist = -1;
    vector<int> nodes;
    for (int v = 1; v <= n; ++v) {
        int d = dist[x][v];
        if (d > max_dist) {
            max_dist = d;
            nodes.clear();
            nodes.push_back(v);
        } else if (d == max_dist) {
            nodes.push_back(v);
        }
    }
    return nodes;
}

void solve() {
    int n;
    cin >> n;
    vector<vector<int>> adj(n+1);
    for (int i = 0; i < n-1; ++i) {
        int u, v;
        cin >> u >> v;
        adj[u].push_back(v);
        adj[v].push_back(u);
    }
    auto dist = computeDistances(n, adj);

    // First query: all nodes
    cout << "? " << n;
    for (int i = 1; i <= n; ++i) cout << " " << i;
    cout << endl;
    int x, d;
    cin >> x >> d;

    // Second query: nodes at max distance from x
    vector<int> S = getMaxDistanceNodes(x, dist, n);
    cout << "? " << S.size();
    for (int v : S) cout << " " << v;
    cout << endl;
    int a, sum_a;
    cin >> a >> sum_a;

    // Third query: nodes at max distance from a
    vector<int> T = getMaxDistanceNodes(a, dist, n);
    cout << "? " << T.size();
    for (int v : T) cout << " " << v;
    cout << endl;
    int b, sum_b;
    cin >> b >> sum_b;

    cout << "! " << a << " " << b << endl;
}

