#include <iostream>
#include <unordered_map>
#include <map>
#include <vector>
using namespace std;

struct DSU {
    struct Node {
        int parent;
        int size_x, size_y;
        bool is_x;
        int rank;
        bool has_cycle;
    };

    unordered_map<int, Node> nodes;
    long long total;

    DSU() : total(0) {}

    void add_node(int u, bool is_x) {
        if (nodes.find(u) == nodes.end()) {
            nodes[u] = {u, is_x ? 1 : 0, is_x ? 0 : 1, is_x, 0, false};
            if (is_x) {
                total += nodes[u].size_y;
            } else {
                total += nodes[u].size_x;
            }
        }
    }

    int find(int u) {
        if (nodes[u].parent != u) {
            nodes[u].parent = find(nodes[u].parent);
        }
        return nodes[u].parent;
    }

    bool unite(int u, int v) {
        int root_u = find(u);
        int root_v = find(v);
        if (root_u == root_v) {
            if (nodes[root_u].has_cycle) {
                return false;
            } else {
                nodes[root_u].has_cycle = true;
                total -= nodes[root_u].size_x * nodes[root_u].size_y;
                total += nodes[root_u].size_x * nodes[root_u].size_y;
                return true;
            }
        }

        Node &node_u = nodes[root_u];
        Node &node_v = nodes[root_v];
        if (node_u.rank < node_v.rank) {
            swap(root_u, root_v);
            swap(node_u, node_v);
        }

        total -= node_u.size_x * node_u.size_y;
        total -= node_v.size_x * node_v.size_y;

        node_v.parent = root_u;
        node_u.size_x += node_v.size_x;
        node_u.size_y += node_v.size_y;
        node_u.has_cycle = node_u.has_cycle || node_v.has_cycle;

        if (node_u.rank == node_v.rank) {
            node_u.rank++;
        }

        total += node_u.size_x * node_u.size_y;
        return true;
    }

    bool cut(int u, int v) {
        int root_u = find(u);
        int root_v = find(v);
        if (root_u != root_v) {
            return false;
        }

        if (!nodes[root_u].has_cycle) {
            total -= nodes[root_u].size_x * nodes[root_u].size_y;
            int size_x = nodes[root_u].size_x;
            int size_y = nodes[root_u].size_y;

            nodes[u].size_x = (nodes[u].is_x ? 1 : 0);
            nodes[u].size_y = (nodes[u].is_x ? 0 : 1);
            nodes[u].parent = u;
            nodes[u].has_cycle = false;
            nodes[u].rank = 0;

            nodes[v].size_x = (nodes[v].is_x ? 1 : 0);
            nodes[v].size_y = (nodes[v].is_x ? 0 : 1);
            nodes[v].parent = v;
            nodes[v].has_cycle = false;
            nodes[v].rank = 0;

            total += nodes[u].size_x * nodes[u].size_y;
            total += nodes[v].size_x * nodes[v].size_y;
            return true;
        } else {
            nodes[root_u].has_cycle = (nodes[root_u].size_x * nodes[root_u].size_y) > (nodes[root_u].size_x + nodes[root_u].size_y - 1);
            return false;
        }
    }
};

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int q;
    cin >> q;
    vector<pair<int, int>> queries(q);
    map<pair<int, int>, int> edge_count;
    DSU dsu;

    for (int i = 0; i < q; ++i) {
        int x, y;
        cin >> x >> y;
        queries[i] = {x, -y};

        int &cnt = edge_count[{x, y}];
        cnt++;
        bool present = cnt % 2 == 1;

        if (present) {
            dsu.add_node(x, true);
            dsu.add_node(-y, false);
            dsu.unite(x, -y);
        } else {
            dsu.cut(x, -y);
        }

        cout << dsu.total << " ";
    }
    cout << endl;

    return 0;
}

