typedef long long ll;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);

    int k;
    cin >> k;
    vector<pair<string, int>> patterns(k);
    for (int i = 0; i < k; i++) {
        cin >> patterns[i].first >> patterns[i].second;
    }
    string s;
    cin >> s;

    vector<int> q_pos;
    for (int i = 0; i < s.size(); i++) {
        if (s[i] == '?') {
            q_pos.push_back(i);
        }
    }
    int m = q_pos.size();

    map<vector<pair<int, char>>, int> mask_counts;

    for (auto &[t, c] : patterns) {
        int len = t.size();
        if (len > s.size()) continue;
        for (int j = 0; j <= (int)s.size() - len; j++) {
            bool valid = true;
            vector<pair<int, char>> req;
            for (int p = 0; p < len; p++) {
                char sc = s[j + p];
                char tc = t[p];
                if (sc == '?') {
                    auto it = find(q_pos.begin(), q_pos.end(), j + p);
                    if (it != q_pos.end()) {
                        int var_idx = it - q_pos.begin();
                        req.push_back({var_idx, tc});
                    } else {
                        valid = false;
                        break;
                    }
                } else if (sc != tc) {
                    valid = false;
                    break;
                }
            }
            if (valid) {
                set<int> vars;
                set<char> chars;
                bool ok = true;
                for (auto &[v, ch] : req) {
                    if (vars.count(v) || chars.count(ch)) {
                        ok = false;
                        break;
                    }
                    vars.insert(v);
                    chars.insert(ch);
                }
                if (ok) {
                    mask_counts[req] += c;
                }
            }
        }
    }

    vector<pair<vector<pair<int, char>>, int>> masks;
    for (auto &[vec, cnt] : mask_counts) {
        masks.emplace_back(vec, cnt);
    }

    ll max_sum = 0;
    vector<char> assign(m, 0);
    vector<bool> used(14, false);

    function<void(int, ll)> backtrack = [&](int var_idx, ll sum_so_far) {
        if (var_idx == m) {
            ll total = sum_so_far;
            for (auto &[vec, cnt] : masks) {
                bool ok = true;
                for (auto &[v, c] : vec) {
                    if (assign[v] != c) {
                        ok = false;
                        break;
                    }
                }
                if (ok) {
                    total += cnt;
                }
            }
            max_sum = max(max_sum, total);
            return;
        }

        for (char c = 'a'; c <= 'n'; c++) {
            if (used[c - 'a']) continue;
            used[c - 'a'] = true;
            assign[var_idx] = c;
            backtrack(var_idx + 1, sum_so_far);
            used[c - 'a'] = false;
        }
    };

    backtrack(0, 0);

    cout << max_sum << endl;

    return 0;
}

