#include <bits/stdc++.h>
using namespace std;

vector<vector<int>> adj;

int get_diameter(int start, int a, int b, int n) {
    vector<bool> visited(n+1, false);
    queue<int> q;
    vector<int> dist(n+1, 0);
    int last_node = start;

    q.push(start);
    visited[start] = true;
    dist[start] = 0;

    while (!q.empty()) {
        int u = q.front();
        q.pop();
        last_node = u;
        for (int v : adj[u]) {
            if ((u == a && v == b) || (u == b && v == a)) continue;
            if (!visited[v]) {
                visited[v] = true;
                dist[v] = dist[u] + 1;
                q.push(v);
            }
        }
    }

    fill(visited.begin(), visited.end(), false);
    q.push(last_node);
    visited[last_node] = true;
    dist[last_node] = 0;
    int max_dist = 0;

    while (!q.empty()) {
        int u = q.front();
        q.pop();
        max_dist = dist[u];
        for (int v : adj[u]) {
            if ((u == a && v == b) || (u == b && v == a)) continue;
            if (!visited[v]) {
                visited[v] = true;
                dist[v] = dist[u] + 1;
                q.push(v);
            }
        }
    }

    return max_dist;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n;
    adj.resize(n+1);
    vector<pair<int, int>> edges;
    for (int i = 0; i < n-1; ++i) {
        int u, v;
        cin >> u >> v;
        adj[u].push_back(v);
        adj[v].push_back(u);
        edges.emplace_back(u, v);
    }

    int max_prod = 0;

    // Check all edge splits
    for (auto& e : edges) {
        int u = e.first, v = e.second;
        int d1 = get_diameter(u, u, v, n);
        int d2 = get_diameter(v, u, v, n);
        max_prod = max(max_prod, d1 * d2);
    }

    // Check all nodes
    for (int u = 1; u <= n; ++u) {
        vector<int> diameters;
        for (int v : adj[u]) {
            int d = get_diameter(v, u, v, n);
            diameters.push_back(d);
        }
        sort(diameters.rbegin(), diameters.rend());
        if (diameters.size() >= 2) {
            max_prod = max(max_prod, diameters[0] * diameters[1]);
        }
    }

    cout << max_prod << endl;

    return 0;
}

