#include <bits/stdc++.h>
#include "tickets.h"
#include <vector>
#include <queue>
#include <tuple>
#include <algorithm>
using namespace std;

long long find_maximum(int k, vector<vector<int>> x) {
    int n = x.size();
    int m = x[0].size();

    vector<long long> sum_low(n, 0);
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < k; ++j) {
            sum_low[i] += x[i][j];
        }
    }

    long long total = 0;
    for (long long s : sum_low) total -= s;

    priority_queue<tuple<long long, int, int>> pq; // (delta, color, t)
    for (int i = 0; i < n; ++i) {
        if (k > 0) {
            long long delta = x[i][m-1] + x[i][k-1];
            pq.emplace(delta, i, 0);
        }
    }

    vector<int> u(n, 0);
    int required = (n / 2) * k;

    while (required--) {
        auto [delta, i, t] = pq.top();
        pq.pop();
        total += delta;
        u[i]++;
        t++;
        if (t < k) {
            long long new_delta = x[i][m-1 - t] + x[i][k-1 - t];
            pq.emplace(new_delta, i, t);
        }
    }

    vector<vector<int>> upper_rounds(n);
    vector<int> available_upper(k, n/2);
    for (int i = 0; i < n; ++i) {
        int rem = u[i];
        for (int r = 0; r < k && rem > 0; ++r) {
            if (available_upper[r] > 0) {
                available_upper[r]--;
                upper_rounds[i].push_back(r);
                rem--;
            }
        }
    }

    vector<vector<int>> s(n, vector<int>(m, -1));
    for (int i = 0; i < n; ++i) {
        int u_i = u[i];
        for (int j = 0; j < u_i; ++j) {
            int idx = m - u_i + j;
            s[i][idx] = upper_rounds[i][j];
        }

        vector<int> lower_rounds;
        int pos = 0;
        for (int r = 0; r < k; ++r) {
            if (pos < upper_rounds[i].size() && upper_rounds[i][pos] == r) {
                pos++;
            } else {
                lower_rounds.push_back(r);
            }
        }

        for (int j = 0; j < k - u_i; ++j) {
            s[i][j] = lower_rounds[j];
        }
    }

    allocate_tickets(s);
    return total;
}

