#include <bits/stdc++.h>
using namespace std;

const int MAX = 2e6 + 5;

vector<int> mu(MAX, 0);
vector<vector<int>> divisors(MAX);

void precompute() {
    vector<int> is_prime(MAX, 1);
    is_prime[0] = is_prime[1] = 0;
    for (int i = 2; i < MAX; ++i) {
        if (is_prime[i]) {
            for (int j = i; j < MAX; j += i) {
                if (!divisors[j].empty() && divisors[j].back() == i) continue;
                divisors[j].push_back(i);
            }
            for (int j = i; j < MAX; j += i) {
                is_prime[j] = 0;
            }
        }
    }

    vector<int> cnt(MAX, 0);
    mu[1] = 1;
    for (int i = 2; i < MAX; ++i) {
        int num = i;
        int prime_count = 0;
        bool square_free = true;
        for (int p : divisors[i]) {
            if (num % p == 0) {
                int exp = 0;
                while (num % p == 0) {
                    num /= p;
                    exp++;
                }
                if (exp > 1) {
                    square_free = false;
                    break;
                }
                prime_count++;
            }
        }
        if (square_free) {
            mu[i] = (prime_count % 2 == 0) ? 1 : -1;
        } else {
            mu[i] = 0;
        }
    }
}

int count_coprimes(int d, int L, int R) {
    if (L > R) return 0;
    int res = 0;
    vector<int> factors = divisors[d];
    int n = factors.size();
    for (int mask = 1; mask < (1 << n); ++mask) {
        int bits = __builtin_popcount(mask);
        int prod = 1;
        for (int j = 0; j < n; ++j) {
            if (mask & (1 << j)) {
                prod *= factors[j];
            }
        }
        if (mu[prod] == 0) continue;
        int cnt = R / prod - (L - 1) / prod;
        res += mu[prod] * cnt;
    }
    return res;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);
    precompute();

    int t;
    cin >> t;
    while (t--) {
        int n, m;
        cin >> n >> m;

        long long ans = 0;
        int max_d = min(n, (int)sqrt(m) + 1);

        for (int d = 2; d <= max_d; ++d) {
            int k_max = min(n / d, m / (d * (d - 1)));
            if (k_max < 1) continue;

            for (int k = 1; k <= k_max; ++k) {
                int A = n / (d * k);
                int B = m / (d * k);
                int L = max(1, d - B);
                int R = min(A, d - 1);
                if (L > R) continue;

                int cnt = 0;
                for (int s : divisors[d]) {
                    if (mu[s] == 0) continue;
                    cnt += mu[s] * (R / s - (L - 1) / s);
                }

                ans += cnt;
            }
        }

        cout << ans << '\n';
    }

    return 0;
}

