        pre_a_A[i+1] = pre_a_A[i] + (a[i] == 'A');
        pre_a_T[i+1] = pre_a_T[i] + (a[i] == 'T');
        pre_a_C[i+1] = pre_a_C[i] + (a[i] == 'C');

        pre_b_A[i+1] = pre_b_A[i] + (b[i] == 'A');
        pre_b_T[i+1] = pre_b_T[i] + (b[i] == 'T');
        pre_b_C[i+1] = pre_b_C[i] + (b[i] == 'C');
    }

    pre_trans.assign(3, vector<vector<int>>(3, vector<int>(n+1, 0)));
    for (int i = 0; i < n; i++) {
        int c1 = char_to_idx(a[i]);
        int c2 = char_to_idx(b[i]);
        for (int x = 0; x < 3; x++) {
            for (int y = 0; y < 3; y++) {
                pre_trans[x][y][i+1] = pre_trans[x][y][i];
                if (x == c1 && y == c2) {
                    pre_trans[x][y][i+1]++;
                }
            }
        }
    }
}

int get_distance(int x, int y) {
    int a_A = pre_a_A[y+1] - pre_a_A[x];
    int a_T = pre_a_T[y+1] - pre_a_T[x];
    int a_C = pre_a_C[y+1] - pre_a_C[x];

    int b_A = pre_b_A[y+1] - pre_b_A[x];
    int b_T = pre_b_T[y+1] - pre_b_T[x];
    int b_C = pre_b_C[y+1] - pre_b_C[x];

    if (a_A != b_A || a_T != b_T || a_C != b_C) {
        return -1;
    }

    int trans[3][3] = {0};
    for (int c1 = 0; c1 < 3; c1++) {
        for (int c2 = 0; c2 < 3; c2++) {
            trans[c1][c2] = pre_trans[c1][c2][y+1] - pre_trans[c1][c2][x];
        }
    }

    int step1 = 0;
    for (int c1 = 0; c1 < 3; c1++) {
        for (int c2 = c1 + 1; c2 < 3; c2++) {
            int m = min(trans[c1][c2], trans[c2][c1]);
            step1 += m;
            trans[c1][c2] -= m;
            trans[c2][c1] -= m;
        }
    }

    int total_remaining = 0;
    for (int c1 = 0; c1 < 3; c1++) {
        for (int c2 = 0; c2 < 3; c2++) {
            if (c1 != c2) {
                total_remaining += trans[c1][c2];
            }
        }
    }

    if (total_remaining % 3 != 0) {
        return -1;
    }
    int step2 = (total_remaining / 3) * 2;

    return step1 + step2;
}

